import { prompt } from 'enquirer';
import { isCI } from './is-ci';
import { logger } from './logger';
import { 
  getIdePluginInstallationPreference, 
  setIdePluginInstallationPreference 
} from './user-config';

export interface PluginInstallationChoice {
  install: boolean;
  remember?: boolean;
}

/**
 * Determines whether the environment supports interactive prompts
 */
function canPrompt(): boolean {
  return (
    !isCI() &&
    !!process.stdout.isTTY &&
    process.env.NODE_ENV !== 'test' &&
    process.env.NX_SKIP_IDE_PLUGIN_PROMPT !== 'true'
  );
}

/**
 * Prompts the user about IDE plugin installation with options to remember the choice
 */
export async function promptForIdePluginInstallation(
  pluginName: string = 'Nx Console'
): Promise<PluginInstallationChoice> {
  // Check if user has already set a preference
  const preference = getIdePluginInstallationPreference();

  if (preference === 'always') {
    return { install: true };
  }

  if (preference === 'never') {
    return { install: false };
  }

  // If we can't prompt (CI, non-TTY, etc.), default to not installing
  if (!canPrompt()) {
    return { install: false };
  }

  try {
    const response = await prompt<{
      action: string;
    }>({
      type: 'select',
      name: 'action',
      message: `Would you like to install the ${pluginName} extension for your IDE?`,
      choices: [
        {
          name: 'yes',
          message: 'Yes, install it now',
          value: 'yes'
        },
        {
          name: 'no', 
          message: 'No, not now',
          value: 'no'
        },
        {
          name: 'always',
          message: 'Yes, and always install it automatically',
          value: 'always'
        },
        {
          name: 'never',
          message: 'No, and never ask me again',
          value: 'never'
        }
      ],
      initial: 0
    });

    const install = response.action === 'yes' || response.action === 'always';

    // Save preference if user chose always or never
    if (response.action === 'always') {
      setIdePluginInstallationPreference('always');
    } else if (response.action === 'never') {
      setIdePluginInstallationPreference('never');
    }

    return {
      install,
      remember: response.action === 'always' || response.action === 'never'
    };
  } catch (error) {
    // If prompting fails for any reason, fall back to not installing
    logger.debug('Failed to prompt for IDE plugin installation:', error.message);
    return { install: false };
  }
}

/**
 * Handles IDE plugin installation with user consent
 * This replaces the automatic installation with a prompt-based approach
 */
export async function handleIdePluginInstallation(): Promise<void> {
  // Skip if environment variable is set to bypass the check entirely
  if (process.env.NX_SKIP_VSCODE_EXTENSION_INSTALL === 'true') {
    return;
  }

  const choice = await promptForIdePluginInstallation();

  if (choice.install) {
    try {
      // Import the native function only when we need it
      const { ensureNxConsoleInstalled } = require('../native');
      ensureNxConsoleInstalled();

      if (choice.remember) {
        logger.info('Your IDE plugin preference has been saved. You can change it by deleting ~/.nx/config.json');
      }
    } catch (error) {
      logger.debug('Failed to install IDE plugin:', error.message);
    }
  }
}

