import { logger } from './logger';

export interface VsCodeExtensionResult {
  success: boolean;
  message: string;
  alreadyInstalled?: boolean;
}

let ensureNxConsoleInstalledNative: () => VsCodeExtensionResult;

try {
  ensureNxConsoleInstalledNative = require('../native').ensureNxConsoleInstalled;
} catch {
  // Native module not available or function not exported yet
  ensureNxConsoleInstalledNative = () => ({
    success: false,
    message: 'Native VSCode extension installer not available',
    alreadyInstalled: undefined,
  });
}

/**
 * Ensures the Nx Console VSCode extension is installed.
 * This is a no-op if the extension is already installed.
 * 
 * @returns Promise<VsCodeExtensionResult> Result of the installation attempt
 */
export async function ensureNxConsoleInstalled(): Promise<VsCodeExtensionResult> {
  try {
    return ensureNxConsoleInstalledNative();
  } catch (error) {
    logger.warn('Failed to check/install Nx Console extension:', error.message);
    return {
      success: false,
      message: error.message || 'Unknown error',
      alreadyInstalled: undefined,
    };
  }
}

/**
 * Silently attempts to install the Nx Console VSCode extension.
 * Does not throw errors or block execution.
 */
export async function tryInstallNxConsole(): Promise<void> {
  try {
    const result = await ensureNxConsoleInstalled();
    if (!result.success && !result.alreadyInstalled) {
      // Log at debug level to avoid noise
      logger.verbose('Nx Console extension installation skipped:', result.message);
    }
  } catch {
    // Silently ignore any errors
  }
}

