 35	  if (
 36	    process.argv[2] !== 'report' &&
 37	    process.argv[2] !== '--version' &&
 38	    process.argv[2] !== '--help' &&
 39	    process.argv[2] !== 'reset'
 40	  ) {
 41	    assertSupportedPlatform();
 42	  }
 43	
 44	  require('nx/src/utils/perf-logging');
 45	
 46	  const workspace = findWorkspaceRoot(process.cwd());
 47	
 48	  performance.mark('loading dotenv files:start');
 49	  if (workspace) {
 50	    loadRootEnvFiles(workspace.dir);
 51	  }
 52	  performance.mark('loading dotenv files:end');
 53	  performance.measure(
 54	    'loading dotenv files',
 55	    'loading dotenv files:start',
 56	    'loading dotenv files:end'
 57	  );
 58	
