use std::process::Command;
use napi::Error;

const NX_CONSOLE_EXTENSION_ID: &str = "nrwl.angular-console";


pub fn get_vscode_command() -> Vec<&'static str> {
    #[cfg(target_os = "windows")]
    return vec!["cursor.cmd", "code.cmd"];

    #[cfg(not(target_os = "windows"))]
    return vec!["cursor", "code"];
}


fn install_extension(commands: &[&str]) -> Result<(), String> {
    for command in commands {
        let output = match Command::new(command)
            .arg("--install-extension")
            .arg(NX_CONSOLE_EXTENSION_ID)
            .arg("--force")
            .output()
        {
            Ok(output) => output,
            Err(e) => {
                // Command not found - try next command
                if e.kind() == std::io::ErrorKind::NotFound {
                    continue;
                }
                // Other errors - try next command
                continue;
            }
        };

        if output.status.success() {
            return Ok(());
        }

        let stderr = String::from_utf8_lossy(&output.stderr);
        let stdout = String::from_utf8_lossy(&output.stdout);

        // Check for "already installed" message in stdout or stderr
        let combined_output = format!("{} {}", stdout, stderr);
        if combined_output.contains("already installed") {
            return Ok(());
        }

        // Try next command for other errors
    }

    // All commands failed or not found - this is OK
    Ok(())
}

#[napi]
pub fn ensure_nx_console_installed() -> Result<(), Error> {
    // Check if installation is disabled via environment variable
    if std::env::var("NX_SKIP_VSCODE_EXTENSION_INSTALL")
        .map(|v| v == "true")
        .unwrap_or(false)
    {
        return Ok(());
    }

    // Get VS Code/Cursor commands for the platform
    let commands = get_vscode_command();

    // Try to install the extension
    // This will succeed even if VS Code/Cursor is not installed or extension already exists
    install_extension(&commands)
        .map_err(Error::from_reason)
}

