 92	/// You can set log levels and different logs by setting the `NX_NATIVE_LOGGING` environment variable
 93	/// Examples:
 94	/// - `NX_NATIVE_LOGGING=trace|warn|debug|error|info` - enable all logs for all crates and modules
 95	/// - `NX_NATIVE_LOGGING=nx=trace` - enable all logs for the `nx` (this) crate
 96	/// - `NX_NATIVE_LOGGING=nx::native::tasks::hashers::hash_project_files=trace` - enable all logs for the `hash_project_files` module
 97	/// - `NX_NATIVE_LOGGING=[{project_name=project}]` - enable logs that contain the project in its span
 98	/// NX_NATIVE_FILE_LOGGING acts the same but logs to .nx/workspace-data/nx.log instead of stdout
 99	pub(crate) fn enable_logger() {
100	    let stdout_layer = tracing_subscriber::fmt::layer()
101	        .with_ansi(std::io::stdout().is_terminal())
102	        .with_writer(std::io::stdout)
103	        .event_format(NxLogFormatter)
104	        .with_filter(
105	            EnvFilter::try_from_env("NX_NATIVE_LOGGING").unwrap_or_else(|_| EnvFilter::new("OFF")),
106	        );
107	
108	    let registry = tracing_subscriber::registry()
109	        .with(stdout_layer)
110	        .with(TuiTracingSubscriberLayer);
111	    tui_logger::init_logger(tui_logger::LevelFilter::Trace).ok();
112	
113	    if env::var("NX_NATIVE_FILE_LOGGING").is_err() {
114	        // File logging is not enabled
115	        registry.try_init().ok();
116	        return;
117	    }
118	
119	    let log_dir = ".nx/workspace-data";
120	
121	    if let Err(e) = create_dir_all(log_dir) {
122	        // Could not create the directory, so we will not log to file
123	        println!(
124	            "Logging to a file was not enabled because Nx could not create the {} directory for logging. Error: {}",
125	            log_dir, e
126	        );
127	        registry.try_init().ok();
128	        return;
129	    };
130	
131	    let file_appender: RollingFileAppender =
132	        RollingFileAppender::new(Rotation::NEVER, log_dir, "nx.log");
133	    let file_layer = tracing_subscriber::fmt::layer()
134	        .with_writer(file_appender)
135	        .event_format(NxLogFormatter)
136	        .with_ansi(false)
137	        .with_filter(
138	            EnvFilter::try_from_env("NX_NATIVE_FILE_LOGGING")
139	                .unwrap_or_else(|_| EnvFilter::new("ERROR")),
140	        );
141	
142	    registry.with(file_layer).try_init().ok();
143	}
