      loadErrors.push(new Error(`Unsupported architecture on Windows: ${process.arch}`))
    }
  } else if (process.platform === 'darwin') {
    try {
        return require('./nx.darwin-universal.node')
      } catch (e) {
        loadErrors.push(e)
      }
