// Skip if explicitly disabled
if skip_install {
    debug!("Nx Console extension installation disabled via NX_SKIP_VSCODE_EXTENSION_INSTALL");
    return;
}

// Check which VS Code variant we're running in
match term_program.as_str() {
    "vscode" => {
        debug!("Running in VS Code terminal, attempting to install Nx Console extension");

        #[cfg(target_os = "windows")]
        let command = "code.cmd";
        #[cfg(not(target_os = "windows"))]
        let command = "code";

        if let Err(e) = install_nx_console(command) {
            debug!("Failed to install Nx Console extension: {}", e);
        }
    }
    _ => {
        // For non-VS Code terminals, try to detect and install for available editors
        debug!("Not running in VS Code terminal (TERM_PROGRAM={}), trying available VS Code variants", term_program);

        #[cfg(target_os = "windows")]
        let commands = [
            ("Cursor", "cursor.cmd"),
            ("VS Code", "code.cmd"),
            ("Windsurf", "windsurf.cmd"),
        ];

        #[cfg(not(target_os = "windows"))]
        let commands = [
            ("Cursor", "cursor"),
            ("VS Code", "code"),
            ("Windsurf", "windsurf"),
        ];

        let mut installed = false;
        for (name, command) in commands {
            match install_nx_console(command) {
                Ok(()) => {
                    debug!("Successfully installed Nx Console extension via {}", name);
                    installed = true;
                    break;
                }
                Err(e) => {
                    trace!("{} command failed: {}", name, e);
                }
            }
        }

        if !installed {
            trace!("No VS Code variant available for automatic extension installation");
        }
    }
}

