                    debug!("Successfully installed Nx Console extension via {}", name);
                    installed = true;
                    break;
                }
                Err(e) => {
                    trace!("{} command failed: {}", name, e);
                }
            }
        }

        if !installed {
            trace!("No VS Code variant available for automatic extension installation");
        }
    }
}

