use crate::native::logger::enable_logger;
use napi::Error;
use std::process::Command;
use tracing::{debug, info, trace};

const NX_CONSOLE_EXTENSION_ID: &str = "nrwl.angular-console";

fn install_nx_console(command: &str) -> Result<(), Error> {
    debug!(
        "Attempting to install Nx Console extension with: {} --install-extension {}",
        command, NX_CONSOLE_EXTENSION_ID
    );

    let output = match Command::new(command)
        .arg("--install-extension")
        .arg(NX_CONSOLE_EXTENSION_ID)
        .output()
    {
        Ok(output) => output,
        Err(e) => match e.kind() {
            std::io::ErrorKind::NotFound => {
                debug!(
                    "VS Code command '{}' not found, skipping extension installation",
                    command
                );
                return Ok(());
            }
            _ => {
                debug!("Failed to execute VS Code command: {}", e);
                return Err(Error::from_reason(format!(
                    "Failed to run command '{}': {}",
                    command, e
                )));
            }
        },
    };

    let stdout = String::from_utf8_lossy(&output.stdout);
    let stderr = String::from_utf8_lossy(&output.stderr);

    if output.status.success() {
        if stdout.contains("already installed") {
            debug!("Nx Console extension is already installed");
        } else {
            info!("Successfully installed Nx Console extension");
        }
        trace!("VS Code output: {}", stdout.trim());
        return Ok(());
    }

    // Check for "already installed" message in stdout or stderr
    let combined_output = format!("{} {}", stdout, stderr);
    if combined_output.contains("already installed") {
        debug!("Nx Console extension is already installed");
        return Ok(());
    }

    // Log the error output for debugging
    debug!("VS Code command failed with status: {:?}", output.status);
    if !stdout.is_empty() {
        trace!("VS Code stdout: {}", stdout.trim());
    }
    if !stderr.is_empty() {
        trace!("VS Code stderr: {}", stderr.trim());
    }

    // All commands failed or not found - this is OK
    Ok(())
}

#[napi]
pub fn ensure_nx_console_installed() {
    enable_logger();
    // Check if installation should be skipped
    let skip_install = std::env::var("NX_SKIP_VSCODE_EXTENSION_INSTALL")
        .map(|v| v == "true")
        .unwrap_or(false);
    let term_program = std::env::var("TERM_PROGRAM").unwrap_or_default();

    if skip_install || term_program != "vscode" {
        trace!(
            "Skipping Nx Console extension installation (NX_SKIP_VSCODE_EXTENSION_INSTALL={}, TERM_PROGRAM={})",
            skip_install, term_program
        );
        return;
    }

    debug!("Running in VS Code terminal, checking Nx Console extension");

    // Try to install the extension
    // This will succeed even if VS Code/Cursor is not installed or extension already exists

    {
        #[cfg(target_os = "windows")]
        let windsurf_command = "windsurf.cmd";
        #[cfg(target_os = "windows")]
        let cursor_command = "cursor.cmd";
        #[cfg(target_os = "windows")]
        let vscode_command = "code.cmd";

        #[cfg(not(target_os = "windows"))]
        let windsurf_command = "windsurf";
        #[cfg(not(target_os = "windows"))]
        let cursor_command = "cursor";
        #[cfg(not(target_os = "windows"))]
        let vscode_command = "code";

        // Try different VS Code variants in order
        let commands = [
            ("Windsurf", windsurf_command),
            ("Cursor", cursor_command),
            ("VS Code", vscode_command),
        ];

        let mut success = false;
        for (name, command) in commands {
            match install_nx_console(command) {
                Ok(()) => {
                    success = true;
                    break;
                }
                Err(e) => {
                    debug!("{} command failed: {}", name, e);
                }
            }
        }

        if !success {
            debug!("Failed to install Nx Console extension with any available command");
        }
    }
}

