            #[cfg(target_os = "windows")]
            { "cursor.cmd" }
            #[cfg(not(target_os = "windows"))]
            { "cursor" }
        }
        SupportedEditor::Windsurf => {
            debug!("Installing Nx Console extension for Windsurf");
            #[cfg(target_os = "windows")]
            { "windsurf.cmd" }
            #[cfg(not(target_os = "windows"))]
            { "windsurf" }
        }
        SupportedEditor::JetBrains => {
            info!("JetBrains IDE detected - please install the Nx Console plugin from the JetBrains marketplace");
            return;
        }
        SupportedEditor::Unknown => {
            trace!("Unknown editor detected, skipping Nx Console extension installation");
            return;
        }
    };

    // Try to install the extension
    if let Err(e) = install_nx_console(command) {
        debug!("Failed to install Nx Console extension: {}", e);
    }
}

