use std::env;
use std::path::{Path, PathBuf};

pub const NX_CONFIG_DIR_NAME: &str = ".nx";
const NX_CLOUD_CONFIG_FILE_NAME: &str = "nxcloud.ini";
const DOT_NX_CLOUD_CONFIG_FILE_NAME: &str = ".nxcloud.ini";

/*
On Windows, we first check to see if the user has a config file in either
- %LOCALAPPDATA%\nxcloud\nxcloud.ini
- %USERPROFILE%\.nxcloud.ini

For Unix-based systems, we check to see if the user has configured a config either
- $XDG_CONFIG_HOME/nxcloud/nxcloud.ini
- $HOME/.config/nxcloud/nxcloud.ini
- $HOME/.nxcloud.ini
*/
pub(super) fn get_user_config_dir(home_dir: impl AsRef<Path>) -> PathBuf {
    if cfg!(target_os = "windows") {
        home_dir.as_ref().join(NX_CONFIG_DIR_NAME)
    } else {
        env::var("XDG_CONFIG_HOME")
            .map_or(home_dir.as_ref(), |s| PathBuf::from(s).as_path())
            .join(".config")
            .join(NX_CONFIG_DIR_NAME)
    }
}

