use crate::native::config::{ensure_config_dir_exists, get_console_preferences_path};
use napi::Error;
use serde::{Deserialize, Serialize};
use std::fs;
use tracing::{debug, warn};

#[derive(Debug, Serialize, Deserialize)]
pub struct NxConsolePreferences {
    pub auto_install_console: Option<bool>,
    pub last_prompted: Option<String>, // ISO date string
}

impl Default for NxConsolePreferences {
    fn default() -> Self {
        Self {
            auto_install_console: None,
            last_prompted: None,
        }
    }
}

impl NxConsolePreferences {
    pub fn load() -> Self {
        match get_console_preferences_path() {
            Ok(path) => {
                if path.exists() {
                    match fs::read_to_string(&path) {
                        Ok(content) => {
                            match serde_json::from_str::<NxConsolePreferences>(&content) {
                                Ok(prefs) => {
                                    debug!("Loaded console preferences from {:?}", path);
                                    return prefs;
                                }
                                Err(e) => {
                                    warn!("Failed to parse console preferences: {}", e);
                                }
                            }
                        }
                        Err(e) => {
                            debug!("Failed to read console preferences file: {}", e);
                        }
                    }
                }
            }
            Err(e) => {
                warn!("Failed to get console preferences path: {}", e);
            }
        }

        Self::default()
    }

    pub fn save(&self) -> Result<(), Error> {
        ensure_config_dir_exists()
            .map_err(|e| Error::from_reason(format!("Failed to create config directory: {}", e)))?;

        let path = get_console_preferences_path()
            .map_err(|e| Error::from_reason(format!("Failed to get preferences path: {}", e)))?;

        let content = serde_json::to_string_pretty(self)
            .map_err(|e| Error::from_reason(format!("Failed to serialize preferences: {}", e)))?;

        fs::write(&path, content)
            .map_err(|e| Error::from_reason(format!("Failed to write preferences file: {}", e)))?;

        debug!("Saved console preferences to {:?}", path);
        Ok(())
    }

    pub fn should_prompt_for_install(&self) -> bool {
        // If user has already made a choice, respect it
        if self.auto_install_console.is_some() {
            return false;
        }

        // For now, always prompt if no preference is set
        // In the future, we could add logic to avoid prompting too frequently
        true
    }

    pub fn set_auto_install_preference(&mut self, auto_install: bool) {
        self.auto_install_console = Some(auto_install);
        self.last_prompted = Some(chrono::Utc::now().to_rfc3339());
    }

    pub fn wants_auto_install(&self) -> bool {
        self.auto_install_console.unwrap_or(false)
    }
}

