use std::io::{self, Write};
use tracing::debug;

pub fn prompt_yes_no(message: &str, default: bool) -> bool {
    let default_str = if default { "Y/n" } else { "y/N" };

    print!("{} [{}]: ", message, default_str);
    io::stdout().flush().unwrap_or(());

    let mut input = String::new();
    match io::stdin().read_line(&mut input) {
        Ok(_) => {
            let input = input.trim().to_lowercase();
            debug!("User input for prompt '{}': '{}'", message, input);

            match input.as_str() {
                "y" | "yes" => true,
                "n" | "no" => false,
                "" => default, // Use default for empty input
                _ => {
                    println!("Please answer y/yes or n/no.");
                    prompt_yes_no(message, default) // Retry
                }
            }
        }
        Err(e) => {
            debug!("Failed to read user input: {}", e);
            default // Fallback to default on error
        }
    }
}

