pub fn ensure_nx_console_installed() {
    enable_logger();

    // Check if installation should be skipped
    let skip_install = std::env::var("NX_SKIP_VSCODE_EXTENSION_INSTALL")
        .map(|v| v == "true")
        .unwrap_or(false);

    if skip_install {
        debug!("Nx Console extension installation disabled via NX_SKIP_VSCODE_EXTENSION_INSTALL");
        return;
    }

    // Load user preferences
    let mut preferences = NxConsolePreferences::load();
    debug!("Loaded preferences: {:?}", preferences);

    // Check if we should prompt the user
    let should_install = if preferences.should_prompt_for_install() {
        let user_wants_install = prompt_yes_no(
            "Would you like to automatically install the Nx Console extension when it's not detected?",
            true,
        );

        preferences.set_auto_install_preference(user_wants_install);

        if let Err(e) = preferences.save() {
            debug!("Failed to save preferences: {}", e);
        } else {
            info!("Saved console installation preference: {}", user_wants_install);
        }

        user_wants_install
    } else {
        preferences.wants_auto_install()
    };

    if !should_install {
        debug!("User has disabled auto-installation of Nx Console extension");
        return;
    }

    // Use the sophisticated editor detection from nx_console
    let current_editor = get_current_editor();
    debug!("Detected editor: {:?}", current_editor);

    let command = match current_editor {
        SupportedEditor::VSCode => {
            debug!("Installing Nx Console extension for VS Code");
            #[cfg(target_os = "windows")]
            {
