pub fn get_nx_config_dir() -> io::Result<PathBuf> {
    let home_dir = env::var("HOME")
        .or_else(|_| env::var("USERPROFILE"))
        .map_err(|_| io::Error::new(io::ErrorKind::NotFound, "Unable to find home directory"))?;

    Ok(get_user_config_dir(PathBuf::from(home_dir)))
}

pub fn get_console_preferences_path() -> io::Result<PathBuf> {
    let config_dir = get_nx_config_dir()?;
    Ok(config_dir.join(NX_CONSOLE_PREFERENCES_FILE_NAME))
}

pub fn ensure_config_dir_exists() -> io::Result<PathBuf> {
    let config_dir = get_nx_config_dir()?;
    if !config_dir.exists() {
        fs::create_dir_all(&config_dir)?;
    }
    Ok(config_dir)
}

