+ */
+export type AnyCompilerPlugin =
+  | CompilerPlugin
+  | ((
+      options?: Record<string, unknown>,
+      program?: Program
+    ) => TransformerFactory)
+  | {
+      before: (
+        options?: Record<string, unknown>,
+        program?: Program
+      ) => TransformerFactory;
+    }
+  | {
+      after: (
+        options?: Record<string, unknown>,
+        program?: Program
+      ) => TransformerFactory;
+    }
+  | {
+      afterDeclarations: (
+        options?: Record<string, unknown>,
+        program?: Program
+      ) => TransformerFactory;
+    };
+
 export interface CompilerPluginHooks {
