  1â†’import { CallToolResult } from '@modelcontextprotocol/sdk/types.js';
  2â†’import { NxConsoleTelemetryLogger } from '@nx-console/shared-telemetry';
  3â†’import { McpServer } from '@modelcontextprotocol/sdk/server/mcp.js';
  4â†’import { Logger } from '@nx-console/shared-utils';
  5â†’import {
  6â†’  getNxCloudTerminalOutput,
  7â†’  getRecentCIPEData,
  8â†’  getPipelineExecutionsSearch,
  9â†’  getPipelineExecutionDetails,
 10â†’} from '@nx-console/shared-nx-cloud';
 11â†’import { z } from 'zod';
 12â†’import {
 13â†’  NX_CLOUD_CIPE_DETAILS,
 14â†’  NX_CLOUD_CIPE_FAILURE,
 15â†’  NX_CLOUD_PIPELINE_EXECUTIONS_SEARCH,
 16â†’  NX_CLOUD_PIPELINE_EXECUTIONS_DETAILS,
 17â†’} from '@nx-console/shared-llm-context/src/lib/tool-names';
 18â†’
 19â†’export function registerNxCloudTools(
 20â†’  workspacePath: string,
 21â†’  server: McpServer,
 22â†’  logger: Logger,
 23â†’  telemetry?: NxConsoleTelemetryLogger,
 24â†’  getGitDiffs?: (
 25â†’    workspacePath: string,
 26â†’    baseSha?: string,
 27â†’    headSha?: string,
 28â†’  ) => Promise<{ path: string; diffContent: string }[] | null>,
 29â†’) {
 30â†’  server.tool(
 31â†’    NX_CLOUD_CIPE_DETAILS,
 32â†’    'Returns a list of CIPE (CI pipeline execution) details for the current workspace and branch from Nx Cloud. This includes the status, and execution ID or link ID. If there are failed tasks, it will also include the task ID. If this returns text that contains "canceled", that means that there were no failures, and additional help and details are not needed.',
 33â†’    {
 34â†’      destructiveHint: false,
 35â†’      readOnlyHint: true,
 36â†’      openWorldHint: true,
 37â†’    },
 38â†’    nxCloudCipeDetails(workspacePath, logger, telemetry),
 39â†’  );
 40â†’  server.tool(
 41â†’    NX_CLOUD_CIPE_FAILURE,
 42â†’    'Returns details about the failure of a CI pipeline execution. When given a execution ID or link ID and a task ID, the terminal output and affected git files will be returned.',
 43â†’    nxCloudFixCipeSchema.shape,
 44â†’    {
 45â†’      destructiveHint: false,
 46â†’      readOnlyHint: true,
 47â†’      openWorldHint: true,
 48â†’    },
 49â†’    nxCloudCipeAffectedFilesAndTerminalOutput(
 50â†’      workspacePath,
 51â†’      logger,
 52â†’      telemetry,
 53â†’      getGitDiffs ?? (async () => null),
 54â†’    ),
 55â†’  );
 56â†’
 57â†’  // Pipeline Executions Search
 58â†’  server.tool(
 59â†’    NX_CLOUD_PIPELINE_EXECUTIONS_SEARCH,
 60â†’    'Search for pipeline executions in Nx Cloud. Returns a list of pipeline executions matching the search criteria.',
 61â†’    pipelineExecutionSearchSchema.shape,
 62â†’    {
 63â†’      destructiveHint: false,
 64â†’      readOnlyHint: true,
 65â†’      openWorldHint: true,
 66â†’    },
 67â†’    nxCloudPipelineExecutionsSearch(workspacePath, logger, telemetry),
 68â†’  );
 69â†’
 70â†’  // Pipeline Execution Details
 71â†’  server.tool(
 72â†’    NX_CLOUD_PIPELINE_EXECUTIONS_DETAILS,
 73â†’    'Get detailed information about a specific pipeline execution in Nx Cloud.',
 74â†’    pipelineExecutionDetailsSchema.shape,
 75â†’    {
 76â†’      destructiveHint: false,
 77â†’      readOnlyHint: true,
 78â†’      openWorldHint: true,
 79â†’    },
 80â†’    nxCloudPipelineExecutionDetails(workspacePath, logger, telemetry),
 81â†’  );
 82â†’}
 83â†’
 84â†’const nxCloudCipeDetails =
 85â†’  (
 86â†’    workspacePath: string,
 87â†’    logger: Logger,
 88â†’    telemetry: NxConsoleTelemetryLogger | undefined,
 89â†’  ) =>
 90â†’  async () => {
 91â†’    telemetry?.logUsage('ai.tool-call', {
 92â†’      tool: 'nx_cloud_cipe_details',
 93â†’    });
 94â†’
 95â†’    const recentData = await getRecentCIPEData(workspacePath, logger);
 96â†’
 97â†’    if (recentData.error) {
 98â†’      throw new Error(
 99â†’        `Error getting recent CIPE data: ${recentData.error.message}`,
100â†’      );
101â†’    }
102â†’
103â†’    const content: CallToolResult['content'] = [];
104â†’
105â†’    content.push({
106â†’      type: 'text',
107â†’      text: `Nx Cloud Workspace Url: ${recentData.workspaceUrl}`,
108â†’    });
109â†’
110â†’    if (recentData.info && recentData.info.length > 0) {
111â†’      content.push({
112â†’        type: 'text',
113â†’        text: `Recent CI Pipeline Executions:`,
114â†’      });
115â†’      for (const info of recentData.info) {
116â†’        content.push({
117â†’          type: 'text',
118â†’          text: `- ${info.cipeUrl} (CIPE Status: ${info.status})`,
119â†’        });
120â†’        for (const runGroup of info.runGroups) {
121â†’          content.push({
122â†’            type: 'text',
123â†’            text: `  -- Run Group: ${runGroup.runGroup} (Run Group Status: ${runGroup.status})`,
124â†’          });
125â†’          for (const run of runGroup.runs) {
126â†’            let runPrompt = '    --- Run';
127â†’            if (run.executionId) {
128â†’              runPrompt += ` Execution ID: ${run.executionId}`;
129â†’            }
130â†’            if (run.linkId) {
131â†’              runPrompt += ` Link ID: ${run.linkId}`;
132â†’            }
133â†’
134â†’            runPrompt += ` (Run Status: ${run.status})`;
135â†’
136â†’            content.push({
137â†’              type: 'text',
138â†’              text: runPrompt,
139â†’            });
140â†’            for (const task of run.failedTasks ?? []) {
141â†’              content.push({
142â†’                type: 'text',
143â†’                text: `      ---- Failed Task: ${task}`,
144â†’              });
145â†’            }
146â†’            if (run.status === 'CANCELED') {
147â†’              content.push({
148â†’                type: 'text',
149â†’                text: `      ---- Note: This run was canceled, indicating no failures occurred.`,
150â†’              });
151â†’            }
152â†’          }
153â†’        }
154â†’      }
155â†’    } else {
156â†’      content.push({
157â†’        type: 'text',
158â†’        text: `No recent CI pipeline executions found. This means that there were no recent runs in the last hour, or there are no runs on the current git branch`,
159â†’      });
160â†’    }
161â†’
162â†’    return {
163â†’      content,
164â†’    } satisfies CallToolResult;
165â†’  };
166â†’
167â†’const nxCloudFixCipeSchema = z.object({
168â†’  executionId: z
169â†’    .string()
170â†’    .optional()
171â†’    .describe('The execution ID of the run in the CI pipeline execution'),
172â†’  linkId: z
173â†’    .string()
174â†’    .optional()
175â†’    .describe('The link ID of the run in the CI pipeline execution'),
176â†’  taskId: z
177â†’    .string()
178â†’    .describe(
179â†’      'The task ID of the failed task, which is a nx task that was executed',
180â†’    ),
181â†’});
