195â†’  async (params: NxCloudFixCipeParams): Promise<CallToolResult> => {
196â†’    telemetry?.logUsage('ai.tool-call', {
197â†’      tool: 'nx_cloud_fix_cipe_failure',
198â†’    });
199â†’
200â†’    if (!params.executionId && !params.linkId) {
