203â†’
204â†’    const content: CallToolResult['content'] = [];
205â†’
206â†’    const terminalOutput = await getNxCloudTerminalOutput(
207â†’      {
208â†’        taskId: params.taskId,
209â†’        ciPipelineExecutionId: params.executionId,
210â†’        linkId: params.linkId,
211â†’      },
212â†’      workspacePath,
213â†’      logger,
214â†’    );
215â†’
216â†’    if (terminalOutput.error) {
217â†’      throw new Error(terminalOutput.error);
218â†’    }
219â†’    content.push({
220â†’      type: 'text',
221â†’      text: `Terminal Output: ${terminalOutput.terminalOutput}`,
222â†’    });
223â†’
224â†’    try {
225â†’      const changedFiles = await getGitDiffs?.(workspacePath);
226â†’      if (changedFiles) {
227â†’        for (const file of changedFiles) {
228â†’          content.push({
229â†’            type: 'text',
230â†’            text: `Changed File: ${file.path}`,
231â†’          });
232â†’          content.push({
233â†’            type: 'text',
234â†’            text: `Diff: ${file.diffContent}`,
235â†’          });
236â†’        }
237â†’      }
