  4	
  5	#[napi::napi]
  6	#[derive(Clone)]
  7	pub struct VsCodeExtensionResult {
  8	    pub success: bool,
  9	    pub message: String,
 10	    pub already_installed: Option<bool>,
 11	}
 12	
 13	pub fn find_vscode_command() -> Result<String, String> {
 14	    // Try the standard 'code' command first
 15	    if is_command_available("code") {
 16	        return Ok("code".to_string());
 17	    }
 18	
 19	    // Platform-specific fallbacks
 20	    #[cfg(target_os = "windows")]
 21	    {
 22	        // Check common Windows paths
 23	        let paths = vec![
 24	            r"C:\Program Files\Microsoft VS Code\bin\code.cmd",
