        assert_eq!(match_process_name("webstorm"), Some(SupportedEditor::JetBrains));
        assert_eq!(match_process_name("random-process"), None);

        // Test case insensitivity
        assert_eq!(match_process_name("CODE"), Some(SupportedEditor::VSCode));
        assert_eq!(match_process_name("CURSOR"), Some(SupportedEditor::Cursor));

        // Test that more specific matches take precedence
        assert_eq!(match_process_name("cursor-code"), Some(SupportedEditor::Cursor));
        assert_eq!(match_process_name("windsurf-code"), Some(SupportedEditor::Windsurf));

        // Test avoiding vscode- helper processes
        assert_eq!(match_process_name("vscode-helper"), None);
        assert_eq!(match_process_name("vscode-server"), None);
    }

