    fn test_detect_from_env_vars_fast() {
        // This test would require setting environment variables
        // For now, just test that it doesn't crash
        let result = detect_from_env_vars_fast();
        // Result can be Some or None depending on environment
        assert!(result.is_some() || result.is_none());
    }

    #[test] 
    fn test_performance_timeout() {
        let start = std::time::Instant::now();
        let _result = detect_editor_optimized();
        let elapsed = start.elapsed();

        // Should complete within reasonable time (100ms)
        assert!(elapsed < std::time::Duration::from_millis(100));
    }

