use std::collections::HashMap;
use std::sync::OnceLock;
use std::time::{Duration, Instant};
use tracing::{debug, trace};

#[derive(Debug, Clone, PartialEq, Eq)]
pub enum SupportedEditor {
    VSCode,
    Cursor,
    Windsurf,
    JetBrains,
    Unknown,
}

static CURRENT_EDITOR: OnceLock<SupportedEditor> = OnceLock::new();

pub fn get_current_editor() -> &'static SupportedEditor {
    CURRENT_EDITOR.get_or_init(|| {
        let start = Instant::now();
        let editor = detect_editor_optimized();
        let elapsed = start.elapsed();

        debug!("Editor detection took {:?}, detected: {:?}", elapsed, editor);
        editor
    })
}

pub fn detect_editor(mut env_map: HashMap<String, String>) -> SupportedEditor {
    let term_editor = if let Some(term) = get_env_var("TERM_PROGRAM", &mut env_map) {
        let term_lower = term.to_lowercase();
        match term_lower.as_str() {
            "vscode" => SupportedEditor::VSCode,
            "cursor" => SupportedEditor::Cursor,
            "windsurf" => SupportedEditor::Windsurf,
            "jetbrains" => SupportedEditor::JetBrains,
            _ => SupportedEditor::Unknown,
        }
    } else {
        SupportedEditor::Unknown
    };

    // For JetBrains, we don't need any additional checks
    if matches!(term_editor, SupportedEditor::JetBrains) {
        return term_editor;
    }

    if matches!(term_editor, SupportedEditor::VSCode) {
        if let Some(vscode_git_var) = get_env_var("VSCODE_GIT_ASKPASS_NODE", &mut env_map) {
            let vscode_git_var_lowercase = vscode_git_var.to_lowercase();
            if vscode_git_var_lowercase.contains("cursor") {
                return SupportedEditor::Cursor;
            } else if vscode_git_var_lowercase.contains("windsurf") {
                return SupportedEditor::Windsurf;
            } else {
                return SupportedEditor::VSCode;
            }
        } else {
            return term_editor;
        }
    }

    SupportedEditor::Unknown
}

fn get_env_var<'a>(name: &str, env_map: &'a mut HashMap<String, String>) -> Option<&'a str> {
    if env_map.contains_key(name) {
        return env_map.get(name).map(|s| s.as_str());
    }

    match std::env::var(name) {
        Ok(val) => {
            env_map.insert(name.to_string(), val);
            env_map.get(name).map(|s| s.as_str())
        }
        Err(_) => None,
    }
}

/// Optimized editor detection with fast fallbacks and process inspection
fn detect_editor_optimized() -> SupportedEditor {
    // 1. Try fast environment variable detection first (~0.1ms)
    if let Some(editor) = detect_from_env_vars_fast() {
        trace!("Editor detected via environment variables: {:?}", editor);
        return editor;
    }

    // 2. Fall back to process inspection with timeout (~5-15ms max)
    if let Some(editor) = detect_from_process_tree() {
        trace!("Editor detected via process inspection: {:?}", editor);
        return editor;
    }

    trace!("No editor detected, returning Unknown");
    SupportedEditor::Unknown
}

/// Fast environment variable detection without allocations
fn detect_from_env_vars_fast() -> Option<SupportedEditor> {
    // Check TERM_PROGRAM first (most reliable)
    if let Ok(term_program) = std::env::var("TERM_PROGRAM") {
        let term_lower = term_program.to_lowercase();
        match term_lower.as_str() {
            "jetbrains" => return Some(SupportedEditor::JetBrains),
            "vscode" => {
                // For VSCode, check the git askpass to differentiate forks
                if let Ok(git_askpass) = std::env::var("VSCODE_GIT_ASKPASS_NODE") {
                    let askpass_lower = git_askpass.to_lowercase();
                    if askpass_lower.contains("cursor") {
                        return Some(SupportedEditor::Cursor);
                    } else if askpass_lower.contains("windsurf") {
                        return Some(SupportedEditor::Windsurf);
                    }
                }
                return Some(SupportedEditor::VSCode);
            }
            "cursor" => {
                // Cursor sets TERM_PROGRAM=vscode, so check git askpass for confirmation
                if let Ok(git_askpass) = std::env::var("VSCODE_GIT_ASKPASS_NODE") {
                    if git_askpass.to_lowercase().contains("cursor") {
                        return Some(SupportedEditor::Cursor);
                    }
                }
            }
            "windsurf" => {
                // Similar to Cursor
                if let Ok(git_askpass) = std::env::var("VSCODE_GIT_ASKPASS_NODE") {
                    if git_askpass.to_lowercase().contains("windsurf") {
                        return Some(SupportedEditor::Windsurf);
                    }
                }
            }
            _ => {}
        }
    }

    // Check other environment variables that might indicate the editor
    if std::env::var("VSCODE_INJECTION").is_ok() {
        return Some(SupportedEditor::VSCode);
    }

    None
}

/// Process tree inspection with platform optimizations and timeout
fn detect_from_process_tree() -> Option<SupportedEditor> {
    let start = Instant::now();
    let timeout = Duration::from_millis(10); // 10ms timeout

    #[cfg(target_os = "linux")]
    {
        detect_from_proc().filter(|_| start.elapsed() < timeout)
    }

    #[cfg(target_os = "macos")]
    {
        detect_from_ps().filter(|_| start.elapsed() < timeout)
    }

    #[cfg(target_os = "windows")]
    {
        detect_from_winapi().filter(|_| start.elapsed() < timeout)
    }

    #[cfg(not(any(target_os = "linux", target_os = "macos", target_os = "windows")))]
    {
        None
    }
}

/// Linux-specific process detection using /proc filesystem
#[cfg(target_os = "linux")]
fn detect_from_proc() -> Option<SupportedEditor> {
    let current_pid = std::process::id();
    let mut pid = current_pid;

    // Walk up the process tree (max 4 levels)
    for _ in 0..4 {
        if let Some(editor) = check_process_name_linux(pid) {
            return Some(editor);
        }

        // Get parent PID from /proc/PID/stat
        if let Ok(parent_pid) = get_parent_pid_linux(pid) {
            if parent_pid == 0 || parent_pid == pid {
                break;
            }
            pid = parent_pid;
        } else {
            break;
        }
    }

    None
}

#[cfg(target_os = "linux")]
fn check_process_name_linux(pid: u32) -> Option<SupportedEditor> {
    // Read process name from /proc/PID/comm
    let comm_path = format!("/proc/{}/comm", pid);
    if let Ok(comm) = std::fs::read_to_string(comm_path) {
        let name = comm.trim().to_lowercase();
        if let Some(editor) = match_process_name(&name) {
            return Some(editor);
        }
    }

    // Also check the command line for more context
    let cmdline_path = format!("/proc/{}/cmdline", pid);
    if let Ok(cmdline) = std::fs::read(cmdline_path) {
        let cmdline_str = String::from_utf8_lossy(&cmdline).to_lowercase();
        if let Some(editor) = match_process_name(&cmdline_str) {
            return Some(editor);
        }
    }

    None
}

#[cfg(target_os = "linux")]
fn get_parent_pid_linux(pid: u32) -> Result<u32, std::io::Error> {
    let stat_path = format!("/proc/{}/stat", pid);
    let stat = std::fs::read_to_string(stat_path)?;

    // The PPID is the 4th field in /proc/PID/stat
    let fields: Vec<&str> = stat.split_whitespace().collect();
    if fields.len() > 3 {
        fields[3].parse().map_err(|_| {
            std::io::Error::new(std::io::ErrorKind::InvalidData, "Invalid PPID")
        })
    } else {
        Err(std::io::Error::new(std::io::ErrorKind::InvalidData, "Malformed stat file"))
    }
}

/// macOS-specific process detection using ps command
#[cfg(target_os = "macos")]
fn detect_from_ps() -> Option<SupportedEditor> {
    use std::process::Command;

    let current_pid = std::process::id();

    // Use ps to get the process tree in one call (more efficient than multiple calls)
    if let Ok(output) = Command::new("ps")
        .args(&["-eo", "pid,ppid,comm", "-p", &current_pid.to_string()])
        .output()
    {
        let output_str = String::from_utf8_lossy(&output.stdout);
        for line in output_str.lines().skip(1) { // Skip header
            let parts: Vec<&str> = line.split_whitespace().collect();
            if parts.len() >= 3 {
                let comm = parts[2].to_lowercase();
                if let Some(editor) = match_process_name(&comm) {
                    return Some(editor);
                }
            }
        }
    }

    None
}

/// Windows-specific process detection using WinAPI
#[cfg(target_os = "windows")]
fn detect_from_winapi() -> Option<SupportedEditor> {
    // For now, fall back to environment variables on Windows
    // Could implement WinAPI calls here if needed
    None
}

/// Match process names to supported editors
fn match_process_name(name: &str) -> Option<SupportedEditor> {
    let name_lower = name.to_lowercase();

    // Order matters: check more specific names first
    if name_lower.contains("cursor") {
        Some(SupportedEditor::Cursor)
    } else if name_lower.contains("windsurf") {
        Some(SupportedEditor::Windsurf)
    } else if name_lower.contains("code") && !name_lower.contains("vscode-") {
        // Avoid matching vscode-related helper processes
        Some(SupportedEditor::VSCode)
    } else if name_lower.contains("idea") || name_lower.contains("intellij") || name_lower.contains("webstorm") || name_lower.contains("pycharm") || name_lower.contains("phpstorm") {
        Some(SupportedEditor::JetBrains)
    } else {
        None
    }
}

