        target: 's',
        project: 'my-app',
      };

      const result = parseRunOneOptions(testCwd, parsedArgs, projectGraph, nxJson);

      expect(result.target).toBe('serve');
    });

    it('should resolve target alias "t" to "test"', () => {
      const parsedArgs = {
        target: 't',
        project: 'my-app',
      };

      const result = parseRunOneOptions(testCwd, parsedArgs, projectGraph, nxJson);

      expect(result.target).toBe('test');
