  1	use std::path::Path;
  2	use std::sync::Arc;
  3	
  4	use anyhow::anyhow;
  5	use interprocess::{
  6	    bound_util::{RefTokioAsyncRead, RefTokioAsyncWrite},
  7	    local_socket::{
  8	        GenericFilePath, ToFsName,
  9	        tokio::{Stream, prelude::*},
 10	    },
 11	};
 12	use jsonrpsee::core::client::{ReceivedMessage, TransportReceiverT, TransportSenderT};
 13	use thiserror::Error;
 14	use tokio::io::{AsyncReadExt, AsyncWriteExt};
 15	
 16	pub struct IpcTransport {
 17	    pub reader: IpcTransportReceiver,
 18	    pub writer: IpcTransportSender,
 19	}
 20	impl IpcTransport {
 21	    pub async fn new(socket_path: &Path) -> Result<Self, anyhow::Error> {
 22	        let socket_path = socket_path.to_fs_name::<GenericFilePath>()?;
 23	        let conn = Stream::connect(socket_path).await?;
 24	        let stream = Arc::new(conn);
 25	        let writer = IpcTransportSender(Arc::clone(&stream));
 26	        let reader = IpcTransportReceiver(Arc::clone(&stream));
 27	        Ok(Self { reader, writer })
 28	    }
 29	}
 30	
 31	pub struct IpcTransportSender(Arc<Stream>);
 32	pub struct IpcTransportReceiver(Arc<Stream>);
 33	
 34	const NEW_LINE: &str = "\r\n";
 35	
 36	#[derive(Debug, Error)]
 37	#[error(transparent)]
 38	pub enum IpcError {
 39	    GenericError(#[from] anyhow::Error),
 40	    IoError(#[from] std::io::Error),
 41	}
 42	
 43	impl TransportSenderT for IpcTransportSender {
 44	    type Error = IpcError;
 45	
 46	    async fn send(&mut self, msg: String) -> Result<(), Self::Error> {
 47	        let mut stream = self.0.as_tokio_async_write();
 48	        let headers = format!("content-length: {}{}{}", msg.len(), NEW_LINE, NEW_LINE);
 49	        stream.write_all(headers.as_bytes()).await?;
 50	        stream.flush().await?;
 51	        let mut msg = msg;
 52	        msg.push_str(NEW_LINE);
 53	        msg.push_str(NEW_LINE);
 54	        stream.write_all(msg.as_bytes()).await?;
 55	        stream.flush().await?;
 56	        Ok(())
 57	    }
 58	}
 59	
 60	impl TransportReceiverT for IpcTransportReceiver {
 61	    type Error = IpcError;
 62	
 63	    async fn receive(&mut self) -> Result<ReceivedMessage, Self::Error> {
 64	        let mut stream = self.0.as_tokio_async_read();
 65	        let mut response_data = Vec::new();
 66	        let mut buffer = [0u8; 1024];
 67	
 68	        loop {
 69	            let bytes_read = stream.read(buffer.as_mut()).await?;
 70	            if bytes_read == 0 {
 71	                break;
 72	            }
 73	            response_data.extend_from_slice(&buffer[..bytes_read]);
 74	
 75	            if let Ok(response_str) = String::from_utf8(response_data.clone()) {
 76	                if response_str.contains('\n') {
 77	                    let parts: Vec<&str> = response_str.split('\n').collect();
 78	                    if let Some(response_part) = parts.first() {
 79	                        return Ok(ReceivedMessage::Text(response_part.to_string()));
 80	                    }
 81	                }
 82	            }
 83	        }
 84	
 85	        Err(anyhow!("Failed to read from IPC stream").into())
 86	    }
 87	}
