 89	#[cfg(test)]
 90	mod test_utils {
 91	    use super::*;
 92	    use std::time::Duration;
 93	    use std::{future::Future, path::PathBuf};
 94	    use tokio::task;
 95	
 96	    // Define trait for platform-specific test setup
 97	    pub trait IpcTestSetup {
 98	        type ServerHandle: Sized;
 99	        type ServerSocket: AsyncReadExt + AsyncWriteExt + Unpin;
100	        type AcceptFuture: Future<Output = Self::ServerSocket> + Send;
101	        type ConnectFuture: Future<Output = Result<IpcTransport, anyhow::Error>> + Send;
102	
103	        // Setup connection paths
104	        fn create_connection_path() -> (PathBuf, PathBuf);
105	
106	        // Create server
107	        fn create_server(path: PathBuf) -> Self::ServerHandle;
108	
109	        // Accept client connection
110	        fn accept_connection(handle: &mut Self::ServerHandle) -> Self::AcceptFuture;
111	
112	        // Connect client
113	        fn connect_client(path: PathBuf) -> Self::ConnectFuture;
114	    }
115	
116	    // Common test implementations
117	    pub async fn test_ipc_transport_connection<T: IpcTestSetup>() {
118	        let (server_path, client_path) = T::create_connection_path();
119	
120	        // Create a mock server
121	        let mut server = T::create_server(server_path);
122	
123	        // Connect in a separate task
124	        let client_task = task::spawn(async move {
125	            // Small delay to ensure server is ready
126	            tokio::time::sleep(Duration::from_millis(100)).await;
127	            T::connect_client(client_path).await
128	        });
129	
130	        // Accept the connection
131	        T::accept_connection(&mut server).await;
132	
133	        let result = client_task.await.unwrap();
134	        assert!(result.is_ok());
135	    }
136	
137	    pub async fn test_transport_sender_send<T: IpcTestSetup>() {
138	        let (server_path, client_path) = T::create_connection_path();
139	
140	        // Create a mock server
141	        let mut server = T::create_server(server_path);
142	
143	        // Start client in background
144	        let client_task = task::spawn(async move {
145	            let mut transport = T::connect_client(client_path).await.unwrap();
146	            transport.writer.send("test message".to_string()).await
147	        });
148	
149	        // Accept the connection and read the message
150	        let mut socket = T::accept_connection(&mut server).await;
151	        let mut buf = [0u8; 1024];
152	        let n = socket.read(&mut buf).await.unwrap();
153	        let received = String::from_utf8_lossy(&buf[0..n]);
154	
155	        assert!(received.contains("content-length: 12"));
156	
157	        let result = client_task.await.unwrap();
158	        assert!(result.is_ok());
159	    }
160	
161	    pub async fn test_transport_receiver_receive<T: IpcTestSetup>() {
162	        let (server_path, client_path) = T::create_connection_path();
163	
164	        // Create a mock server
165	        let mut server = T::create_server(server_path);
166	
167	        // Start client in background
168	        let client_task = task::spawn(async move {
169	            let mut transport = T::connect_client(client_path).await.unwrap();
170	            transport.reader.receive().await
171	        });
172	
173	        // Accept connection and send a message
174	        let mut socket = T::accept_connection(&mut server).await;
175	        let message = "test\nresponse";
176	        socket.write_all(message.as_bytes()).await.unwrap();
177	        socket.flush().await.unwrap();
178	
179	        let result = client_task.await.unwrap();
180	        assert!(result.is_ok());
181	        if let Ok(ReceivedMessage::Text(text)) = result {
182	            assert_eq!(text, "test");
183	        } else {
184	            panic!("Expected Text message");
185	        }
186	    }
187	}
