 9	fn socket_dir_name(workspace_root: &str, unique_name: Option<&'static str>) -> PathBuf {
10	    let mut hashing_string = workspace_root.to_lowercase();
11	    if let Some(name) = unique_name {
12	        hashing_string.push(',');
13	        hashing_string.push_str(name);
14	    }
15	    let result = hash(hashing_string.as_bytes());
16	    let temp_dir = std::env::temp_dir();
17	    temp_dir.join(result)
18	}
19	
20	fn get_socket_dir(workspace_root: &str, unique_name: Option<&'static str>) -> PathBuf {
21	    let dir_path = env::var("NX_SOCKET_DIR")
22	        .or_else(|_| env::var("NX_DAEMON_SOCKET_DIR"))
23	        .map(PathBuf::from)
24	        .unwrap_or_else(|_| socket_dir_name(workspace_root, unique_name));
25	
26	    let path = if cfg!(target_os = "windows") {
27	        dir_path
