const fs = require('fs');

// Read the GitHub issues data from stdin
let data = '';
process.stdin.on('data', chunk => data += chunk);
process.stdin.on('end', () => {
  const issues = JSON.parse(data);

  // Filter only assigned issues
  const assignedIssues = issues.filter(issue => issue.assignees.length > 0);

  // Create analysis structure
  const analysis = {};

  assignedIssues.forEach(issue => {
    issue.assignees.forEach(assignee => {
      const login = assignee.login;

      if (!analysis[login]) {
        analysis[login] = {
          name: assignee.name || login,
          total: 0,
          priorities: {
            'priority: high': 0,
            'priority: medium': 0,
            'priority: low': 0,
            'no priority': 0
          }
        };
      }

      analysis[login].total++;

      // Find priority label
      const priorityLabel = issue.labels.find(label => 
        label.name.startsWith('priority:')
      );

      if (priorityLabel) {
        analysis[login].priorities[priorityLabel.name]++;
      } else {
        analysis[login].priorities['no priority']++;
      }
    });
  });

  // Sort by total issues descending
  const sorted = Object.entries(analysis)
    .sort(([,a], [,b]) => b.total - a.total);

  console.log('# GitHub Issue Distribution Analysis\n');

  sorted.forEach(([login, data]) => {
    console.log(`## ${data.name} (${login})`);
    console.log(`**Total Issues**: ${data.total}`);
    console.log(`- High Priority: ${data.priorities['priority: high']}`);
    console.log(`- Medium Priority: ${data.priorities['priority: medium']}`);
    console.log(`- Low Priority: ${data.priorities['priority: low']}`);
    console.log(`- No Priority: ${data.priorities['no priority']}`);
    console.log('');
  });

  // Summary
  const totals = {
    'priority: high': 0,
    'priority: medium': 0,
    'priority: low': 0,
    'no priority': 0
  };

  Object.values(analysis).forEach(member => {
    Object.entries(member.priorities).forEach(([priority, count]) => {
      totals[priority] += count;
    });
  });

  console.log('## Overall Priority Distribution');
  console.log(`- High Priority: ${totals['priority: high']} issues`);
  console.log(`- Medium Priority: ${totals['priority: medium']} issues`);
  console.log(`- Low Priority: ${totals['priority: low']} issues`);
  console.log(`- No Priority: ${totals['no priority']} issues`);
  console.log(`- **Total Assigned**: ${Object.values(analysis).reduce((sum, m) => sum + m.total, 0)} issues`);
});

