  1	import { existsSync, statSync } from 'fs';
  2	import { createServer, Server, Socket } from 'net';
  3	import { join } from 'path';
  4	import { PerformanceObserver } from 'perf_hooks';
  5	import { hashArray } from '../../hasher/file-hasher';
  6	import { hashFile } from '../../native';
  7	import { consumeMessagesFromSocket } from '../../utils/consume-messages-from-socket';
  8	import { readJsonFile } from '../../utils/fileutils';
  9	import { PackageJson } from '../../utils/package-json';
 10	import { nxVersion } from '../../utils/versions';
 11	import { setupWorkspaceContext } from '../../utils/workspace-context';
 12	import { workspaceRoot } from '../../utils/workspace-root';
 13	import { getDaemonProcessIdSync, writeDaemonJsonProcessCache } from '../cache';
 14	import {
 15	  getFullOsSocketPath,
 16	  isWindows,
 17	  killSocketOrPath,
 18	} from '../socket-utils';
 19	import {
 20	  hasRegisteredFileWatcherSockets,
 21	  registeredFileWatcherSockets,
 22	  removeRegisteredFileWatcherSocket,
 23	} from './file-watching/file-watcher-sockets';
 24	import { handleHashTasks } from './handle-hash-tasks';
 25	import {
 26	  handleOutputsHashesMatch,
 27	  handleRecordOutputsHash,
 28	} from './handle-outputs-tracking';
 29	import { handleProcessInBackground } from './handle-process-in-background';
 30	import { handleRequestProjectGraph } from './handle-request-project-graph';
 31	import { handleRequestShutdown } from './handle-request-shutdown';
 32	import { serverLogger } from './logger';
 33	import {
 34	  disableOutputsTracking,
 35	  processFileChangesInOutputs,
 36	} from './outputs-tracking';
 37	import {
 38	  addUpdatedAndDeletedFiles,
 39	  registerProjectGraphRecomputationListener,
 40	} from './project-graph-incremental-recomputation';
 41	import {
 42	  getOutputWatcherInstance,
 43	  getWatcherInstance,
 44	  handleServerProcessTermination,
 45	  resetInactivityTimeout,
 46	  respondToClient,
 47	  respondWithErrorAndExit,
 48	  SERVER_INACTIVITY_TIMEOUT_MS,
 49	  storeOutputWatcherInstance,
 50	  storeWatcherInstance,
 51	} from './shutdown-utils';
 52	import {
 53	  convertChangeEventsToLogMessage,
 54	  FileWatcherCallback,
 55	  watchOutputFiles,
 56	  watchWorkspace,
 57	} from './watcher';
 58	import { handleGlob, handleMultiGlob } from './handle-glob';
 59	import {
 60	  GLOB,
 61	  isHandleGlobMessage,
 62	  isHandleMultiGlobMessage,
 63	  MULTI_GLOB,
 64	} from '../message-types/glob';
 65	import {
 66	  GET_NX_WORKSPACE_FILES,
 67	  isHandleNxWorkspaceFilesMessage,
 68	} from '../message-types/get-nx-workspace-files';
 69	import { handleNxWorkspaceFiles } from './handle-nx-workspace-files';
 70	import {
 71	  GET_CONTEXT_FILE_DATA,
 72	  isHandleContextFileDataMessage,
 73	} from '../message-types/get-context-file-data';
 74	import { handleContextFileData } from './handle-context-file-data';
 75	import {
 76	  GET_FILES_IN_DIRECTORY,
 77	  isHandleGetFilesInDirectoryMessage,
 78	} from '../message-types/get-files-in-directory';
 79	import { handleGetFilesInDirectory } from './handle-get-files-in-directory';
 80	import {
 81	  HASH_GLOB,
 82	  isHandleHashGlobMessage,
 83	  isHandleHashMultiGlobMessage,
 84	} from '../message-types/hash-glob';
 85	import { handleHashGlob, handleHashMultiGlob } from './handle-hash-glob';
 86	import {
 87	  GET_ESTIMATED_TASK_TIMINGS,
 88	  GET_FLAKY_TASKS,
 89	  isHandleGetEstimatedTaskTimings,
 90	  isHandleGetFlakyTasksMessage,
 91	  isHandleWriteTaskRunsToHistoryMessage,
 92	  RECORD_TASK_RUNS,
 93	} from '../message-types/task-history';
 94	import {
 95	  handleRecordTaskRuns,
 96	  handleGetFlakyTasks,
 97	  handleGetEstimatedTaskTimings,
 98	} from './handle-task-history';
 99	import { isHandleForceShutdownMessage } from '../message-types/force-shutdown';
100	import { handleForceShutdown } from './handle-force-shutdown';
101	import {
102	  GET_SYNC_GENERATOR_CHANGES,
103	  isHandleGetSyncGeneratorChangesMessage,
104	} from '../message-types/get-sync-generator-changes';
105	import { handleGetSyncGeneratorChanges } from './handle-get-sync-generator-changes';
106	import { collectAndScheduleSyncGenerators } from './sync-generators';
107	import {
108	  GET_REGISTERED_SYNC_GENERATORS,
109	  isHandleGetRegisteredSyncGeneratorsMessage,
110	} from '../message-types/get-registered-sync-generators';
111	import { handleGetRegisteredSyncGenerators } from './handle-get-registered-sync-generators';
112	import {
113	  UPDATE_WORKSPACE_CONTEXT,
114	  isHandleUpdateWorkspaceContextMessage,
115	} from '../message-types/update-workspace-context';
116	import { handleUpdateWorkspaceContext } from './handle-update-workspace-context';
117	import {
118	  FLUSH_SYNC_GENERATOR_CHANGES_TO_DISK,
119	  isHandleFlushSyncGeneratorChangesToDiskMessage,
120	} from '../message-types/flush-sync-generator-changes-to-disk';
121	import { handleFlushSyncGeneratorChangesToDisk } from './handle-flush-sync-generator-changes-to-disk';
122	import {
123	  isHandlePostTasksExecutionMessage,
124	  isHandlePreTasksExecutionMessage,
125	  POST_TASKS_EXECUTION,
126	  PRE_TASKS_EXECUTION,
127	} from '../message-types/run-tasks-execution-hooks';
128	import {
129	  handleRunPostTasksExecution,
130	  handleRunPreTasksExecution,
131	} from './handle-tasks-execution-hooks';
132	
133	let performanceObserver: PerformanceObserver | undefined;
134	let workspaceWatcherError: Error | undefined;
135	let outputsWatcherError: Error | undefined;
136	
137	global.NX_DAEMON = true;
138	
139	export type HandlerResult = {
140	  description: string;
141	  error?: any;
142	  response?: string;
143	};
144	
145	let numberOfOpenConnections = 0;
146	export const openSockets: Set<Socket> = new Set();
147	
148	const server = createServer(async (socket) => {
149	  numberOfOpenConnections += 1;
150	  openSockets.add(socket);
151	  serverLogger.log(
152	    `Established a connection. Number of open connections: ${numberOfOpenConnections}`
153	  );
154	  resetInactivityTimeout(handleInactivityTimeout);
155	  if (!performanceObserver) {
156	    performanceObserver = new PerformanceObserver((list) => {
157	      const entry = list.getEntries()[0];
158	      serverLogger.log(`Time taken for '${entry.name}'`, `${entry.duration}ms`);
159	    });
160	    performanceObserver.observe({ entryTypes: ['measure'] });
161	  }
162	
163	  socket.on(
164	    'data',
165	    consumeMessagesFromSocket(async (message) => {
166	      await handleMessage(socket, message);
167	    })
168	  );
169	
170	  socket.on('error', (e) => {
171	    serverLogger.log('Socket error');
172	    console.error(e);
173	  });
174	
175	  socket.on('close', () => {
176	    numberOfOpenConnections -= 1;
177	    openSockets.delete(socket);
178	    serverLogger.log(
179	      `Closed a connection. Number of open connections: ${numberOfOpenConnections}`
180	    );
181	
182	    removeRegisteredFileWatcherSocket(socket);
183	  });
184	});
185	registerProcessTerminationListeners();
186	
187	async function handleMessage(socket, data: string) {
188	  if (workspaceWatcherError) {
189	    await respondWithErrorAndExit(
190	      socket,
191	      `File watcher error in the workspace '${workspaceRoot}'.`,
192	      workspaceWatcherError
193	    );
194	  }
195	
196	  const outdated = daemonIsOutdated();
197	  if (outdated) {
198	    await respondWithErrorAndExit(
199	      socket,
200	      `Daemon outdated`,
201	      new Error(outdated)
202	    );
203	  }
204	
205	  resetInactivityTimeout(handleInactivityTimeout);
206	
207	  const unparsedPayload = data;
208	  let payload;
209	  try {
210	    payload = JSON.parse(unparsedPayload);
211	  } catch (e) {
212	    await respondWithErrorAndExit(
213	      socket,
214	      `Invalid payload from the client`,
215	      new Error(`Unsupported payload sent to daemon server: ${unparsedPayload}`)
216	    );
217	  }
218	
219	  if (payload.type === 'PING') {
220	    await handleResult(socket, 'PING', () =>
221	      Promise.resolve({ response: JSON.stringify(true), description: 'ping' })
222	    );
223	  } else if (payload.type === 'REQUEST_PROJECT_GRAPH') {
224	    await handleResult(socket, 'REQUEST_PROJECT_GRAPH', () =>
225	      handleRequestProjectGraph()
226	    );
227	  } else if (payload.type === 'HASH_TASKS') {
228	    await handleResult(socket, 'HASH_TASKS', () => handleHashTasks(payload));
229	  } else if (payload.type === 'PROCESS_IN_BACKGROUND') {
230	    await handleResult(socket, 'PROCESS_IN_BACKGROUND', () =>
231	      handleProcessInBackground(payload)
232	    );
233	  } else if (payload.type === 'RECORD_OUTPUTS_HASH') {
234	    await handleResult(socket, 'RECORD_OUTPUTS_HASH', () =>
235	      handleRecordOutputsHash(payload)
236	    );
237	  } else if (payload.type === 'OUTPUTS_HASHES_MATCH') {
238	    await handleResult(socket, 'OUTPUTS_HASHES_MATCH', () =>
239	      handleOutputsHashesMatch(payload)
240	    );
241	  } else if (payload.type === 'REQUEST_SHUTDOWN') {
242	    await handleResult(socket, 'REQUEST_SHUTDOWN', () =>
243	      handleRequestShutdown(server, numberOfOpenConnections)
244	    );
245	  } else if (payload.type === 'REGISTER_FILE_WATCHER') {
246	    registeredFileWatcherSockets.push({ socket, config: payload.config });
247	  } else if (isHandleGlobMessage(payload)) {
248	    await handleResult(socket, GLOB, () =>
249	      handleGlob(payload.globs, payload.exclude)
250	    );
251	  } else if (isHandleMultiGlobMessage(payload)) {
252	    await handleResult(socket, MULTI_GLOB, () =>
253	      handleMultiGlob(payload.globs, payload.exclude)
254	    );
255	  } else if (isHandleNxWorkspaceFilesMessage(payload)) {
256	    await handleResult(socket, GET_NX_WORKSPACE_FILES, () =>
257	      handleNxWorkspaceFiles(payload.projectRootMap)
258	    );
259	  } else if (isHandleGetFilesInDirectoryMessage(payload)) {
260	    await handleResult(socket, GET_FILES_IN_DIRECTORY, () =>
261	      handleGetFilesInDirectory(payload.dir)
262	    );
263	  } else if (isHandleContextFileDataMessage(payload)) {
264	    await handleResult(socket, GET_CONTEXT_FILE_DATA, () =>
265	      handleContextFileData()
266	    );
267	  } else if (isHandleHashGlobMessage(payload)) {
268	    await handleResult(socket, HASH_GLOB, () =>
269	      handleHashGlob(payload.globs, payload.exclude)
270	    );
271	  } else if (isHandleHashMultiGlobMessage(payload)) {
272	    await handleResult(socket, HASH_GLOB, () =>
273	      handleHashMultiGlob(payload.globGroups)
274	    );
275	  } else if (isHandleGetFlakyTasksMessage(payload)) {
276	    await handleResult(socket, GET_FLAKY_TASKS, () =>
277	      handleGetFlakyTasks(payload.hashes)
278	    );
279	  } else if (isHandleGetEstimatedTaskTimings(payload)) {
280	    await handleResult(socket, GET_ESTIMATED_TASK_TIMINGS, () =>
281	      handleGetEstimatedTaskTimings(payload.targets)
282	    );
283	  } else if (isHandleWriteTaskRunsToHistoryMessage(payload)) {
284	    await handleResult(socket, RECORD_TASK_RUNS, () =>
285	      handleRecordTaskRuns(payload.taskRuns)
286	    );
287	  } else if (isHandleForceShutdownMessage(payload)) {
288	    await handleResult(socket, 'FORCE_SHUTDOWN', () =>
289	      handleForceShutdown(server)
290	    );
291	  } else if (isHandleGetSyncGeneratorChangesMessage(payload)) {
292	    await handleResult(socket, GET_SYNC_GENERATOR_CHANGES, () =>
293	      handleGetSyncGeneratorChanges(payload.generators)
294	    );
295	  } else if (isHandleFlushSyncGeneratorChangesToDiskMessage(payload)) {
296	    await handleResult(socket, FLUSH_SYNC_GENERATOR_CHANGES_TO_DISK, () =>
297	      handleFlushSyncGeneratorChangesToDisk(payload.generators)
298	    );
299	  } else if (isHandleGetRegisteredSyncGeneratorsMessage(payload)) {
300	    await handleResult(socket, GET_REGISTERED_SYNC_GENERATORS, () =>
301	      handleGetRegisteredSyncGenerators()
302	    );
303	  } else if (isHandleUpdateWorkspaceContextMessage(payload)) {
304	    await handleResult(socket, UPDATE_WORKSPACE_CONTEXT, () =>
305	      handleUpdateWorkspaceContext(
306	        payload.createdFiles,
307	        payload.updatedFiles,
308	        payload.deletedFiles
309	      )
310	    );
311	  } else if (isHandlePreTasksExecutionMessage(payload)) {
312	    await handleResult(socket, PRE_TASKS_EXECUTION, () =>
313	      handleRunPreTasksExecution(payload.context)
314	    );
315	  } else if (isHandlePostTasksExecutionMessage(payload)) {
316	    await handleResult(socket, POST_TASKS_EXECUTION, () =>
317	      handleRunPostTasksExecution(payload.context)
318	    );
319	  } else {
320	    await respondWithErrorAndExit(
321	      socket,
322	      `Invalid payload from the client`,
323	      new Error(`Unsupported payload sent to daemon server: ${unparsedPayload}`)
324	    );
325	  }
326	}
327	
328	export async function handleResult(
329	  socket: Socket,
330	  type: string,
331	  hrFn: () => Promise<HandlerResult>
332	) {
333	  const startMark = new Date();
334	  const hr = await hrFn();
335	  const doneHandlingMark = new Date();
336	  if (hr.error) {
337	    await respondWithErrorAndExit(socket, hr.description, hr.error);
338	  } else {
339	    await respondToClient(socket, hr.response, hr.description);
340	  }
341	  const endMark = new Date();
342	  serverLogger.log(
343	    `Handled ${type}. Handling time: ${
344	      doneHandlingMark.getTime() - startMark.getTime()
345	    }. Response time: ${endMark.getTime() - doneHandlingMark.getTime()}.`
346	  );
347	}
348	
349	function handleInactivityTimeout() {
350	  if (hasRegisteredFileWatcherSockets()) {
351	    serverLogger.log(
352	      `There are open file watchers. Resetting inactivity timer.`
353	    );
354	    resetInactivityTimeout(handleInactivityTimeout);
355	  } else {
356	    handleServerProcessTermination({
357	      server,
358	      reason: `${SERVER_INACTIVITY_TIMEOUT_MS}ms of inactivity`,
359	      sockets: openSockets,
360	    });
361	  }
362	}
363	
364	function registerProcessTerminationListeners() {
365	  process
366	    .on('SIGINT', () =>
367	      handleServerProcessTermination({
368	        server,
369	        reason: 'received process SIGINT',
370	        sockets: openSockets,
371	      })
372	    )
373	    .on('SIGTERM', () =>
374	      handleServerProcessTermination({
375	        server,
376	        reason: 'received process SIGTERM',
377	        sockets: openSockets,
378	      })
379	    )
380	    .on('SIGHUP', () =>
381	      handleServerProcessTermination({
382	        server,
383	        reason: 'received process SIGHUP',
384	        sockets: openSockets,
385	      })
386	    );
387	}
388	
389	let existingLockHash: string | undefined;
390	
391	function daemonIsOutdated(): string | null {
392	  if (nxVersionChanged()) {
393	    return 'NX_VERSION_CHANGED';
394	  } else if (lockFileHashChanged()) {
395	    return 'LOCK_FILES_CHANGED';
396	  }
397	  return null;
398	}
399	
400	function nxVersionChanged(): boolean {
401	  return nxVersion !== getInstalledNxVersion();
402	}
403	
404	const nxPackageJsonPath = require.resolve('nx/package.json');
405	
406	function getInstalledNxVersion() {
407	  try {
408	    const { version } = readJsonFile<PackageJson>(nxPackageJsonPath);
409	    return version;
410	  } catch (e) {
411	    // node modules are absent, so we can return null, which would shut down the daemon
412	    return null;
413	  }
414	}
415	
416	function lockFileHashChanged(): boolean {
417	  const lockHashes = [
418	    join(workspaceRoot, 'package-lock.json'),
419	    join(workspaceRoot, 'yarn.lock'),
420	    join(workspaceRoot, 'pnpm-lock.yaml'),
421	    join(workspaceRoot, 'bun.lockb'),
422	    join(workspaceRoot, 'bun.lock'),
423	  ]
424	    .filter((file) => existsSync(file))
425	    .map((file) => hashFile(file));
426	  const newHash = hashArray(lockHashes);
427	  if (existingLockHash && newHash != existingLockHash) {
428	    existingLockHash = newHash;
429	    return true;
430	  } else {
431	    existingLockHash = newHash;
432	    return false;
433	  }
434	}
435	
436	/**
437	 * When applicable files in the workspaces are changed (created, updated, deleted),
438	 * we need to recompute the cached serialized project graph so that it is readily
439	 * available for the next client request to the server.
440	 */
441	const handleWorkspaceChanges: FileWatcherCallback = async (
442	  err,
443	  changeEvents
444	) => {
445	  if (workspaceWatcherError) {
446	    serverLogger.watcherLog(
447	      'Skipping handleWorkspaceChanges because of a previously recorded watcher error.'
448	    );
449	    return;
450	  }
451	
452	  try {
453	    resetInactivityTimeout(handleInactivityTimeout);
454	
455	    const outdatedReason = daemonIsOutdated();
456	    if (outdatedReason) {
457	      await handleServerProcessTermination({
458	        server,
459	        reason: outdatedReason,
460	        sockets: openSockets,
461	      });
462	      return;
463	    }
464	
465	    if (err) {
466	      let error = typeof err === 'string' ? new Error(err) : err;
467	      serverLogger.watcherLog(
468	        'Unexpected workspace watcher error',
469	        error.message
470	      );
471	      console.error(error);
472	      workspaceWatcherError = error;
473	      return;
474	    }
475	
476	    serverLogger.watcherLog(convertChangeEventsToLogMessage(changeEvents));
477	
478	    const updatedFilesToHash = [];
479	    const createdFilesToHash = [];
480	    const deletedFiles = [];
481	
482	    for (const event of changeEvents) {
483	      if (event.type === 'delete') {
484	        deletedFiles.push(event.path);
485	      } else {
486	        try {
487	          const s = statSync(join(workspaceRoot, event.path));
488	          if (s.isFile()) {
489	            if (event.type === 'update') {
490	              updatedFilesToHash.push(event.path);
491	            } else {
492	              createdFilesToHash.push(event.path);
493	            }
494	          }
495	        } catch (e) {
496	          // this can happen when the update file was deleted right after
497	        }
498	      }
499	    }
500	
501	    addUpdatedAndDeletedFiles(
502	      createdFilesToHash,
503	      updatedFilesToHash,
504	      deletedFiles
505	    );
506	  } catch (err) {
507	    serverLogger.watcherLog(`Unexpected workspace error`, err.message);
508	    console.error(err);
509	    workspaceWatcherError = err;
510	  }
511	};
512	
513	const handleOutputsChanges: FileWatcherCallback = async (err, changeEvents) => {
514	  try {
515	    if (err || !changeEvents || !changeEvents.length) {
516	      let error = typeof err === 'string' ? new Error(err) : err;
517	      serverLogger.watcherLog(
518	        'Unexpected outputs watcher error',
519	        error.message
520	      );
521	      console.error(error);
522	      outputsWatcherError = error;
523	      disableOutputsTracking();
524	      return;
525	    }
526	    if (outputsWatcherError) {
527	      return;
528	    }
529	
530	    serverLogger.watcherLog('Processing file changes in outputs');
531	    processFileChangesInOutputs(changeEvents);
532	  } catch (err) {
533	    serverLogger.watcherLog(`Unexpected outputs watcher error`, err.message);
534	    console.error(err);
535	    outputsWatcherError = err;
536	    disableOutputsTracking();
537	  }
538	};
539	
540	export async function startServer(): Promise<Server> {
541	  setupWorkspaceContext(workspaceRoot);
542	
543	  // Persist metadata about the background process so that it can be cleaned up later if needed
544	  await writeDaemonJsonProcessCache({
545	    processId: process.pid,
546	  });
547	
548	  // See notes in socket-command-line-utils.ts on OS differences regarding clean up of existings connections.
549	  if (!isWindows) {
550	    killSocketOrPath();
551	  }
552	
553	  return new Promise(async (resolve, reject) => {
554	    try {
555	      server.listen(getFullOsSocketPath(), async () => {
556	        try {
557	          serverLogger.log(`Started listening on: ${getFullOsSocketPath()}`);
558	
559	          setInterval(() => {
560	            if (getDaemonProcessIdSync() !== process.pid) {
561	              return handleServerProcessTermination({
562	                server,
563	                reason: 'this process is no longer the current daemon (native)',
564	                sockets: openSockets,
565	              });
566	            }
567	          }).unref();
568	
569	          // this triggers the storage of the lock file hash
570	          daemonIsOutdated();
571	
572	          if (!getWatcherInstance()) {
573	            storeWatcherInstance(
574	              await watchWorkspace(server, handleWorkspaceChanges)
575	            );
576	
577	            serverLogger.watcherLog(
578	              `Subscribed to changes within: ${workspaceRoot} (native)`
579	            );
580	          }
581	
582	          if (!getOutputWatcherInstance()) {
583	            storeOutputWatcherInstance(
584	              await watchOutputFiles(server, handleOutputsChanges)
585	            );
586	          }
587	
588	          // listen for project graph recomputation events to collect and schedule sync generators
589	          registerProjectGraphRecomputationListener(
590	            collectAndScheduleSyncGenerators
591	          );
592	          // trigger an initial project graph recomputation
593	          addUpdatedAndDeletedFiles([], [], []);
594	
595	          return resolve(server);
596	        } catch (err) {
597	          await handleWorkspaceChanges(err, []);
598	        }
599	      });
600	    } catch (err) {
601	      reject(err);
602	    }
603	  });
604	}
605

