 86	export function convertChangeEventsToLogMessage(
 87	  changeEvents: WatchEvent[]
 88	): string {
 89	  // If only a single file was changed, show the information inline
 90	  if (changeEvents.length === 1) {
 91	    const { path, type } = changeEvents[0];
 92	    let typeLog = 'updated';
 93	    switch (type) {
 94	      case 'create':
 95	        typeLog = 'created or restored';
 96	        break;
 97	      case 'update':
 98	        typeLog = 'modified';
 99	        break;
100	      case 'delete':
101	        typeLog = 'deleted';
102	        break;
103	    }
104	    return `${path} was ${typeLog}`;
105	  }
106	
107	  let numCreatedOrRestoredFiles = 0;
108	  let numModifiedFiles = 0;
109	  let numDeletedFiles = 0;
110	  for (const event of changeEvents) {
111	    switch (event.type) {
112	      case 'create':
113	        numCreatedOrRestoredFiles++;
114	        break;
115	      case 'update':
116	        numModifiedFiles++;
117	        break;
118	      case 'delete':
