 16â†’import { getGraphService } from '../machines/graph.service';
 17â†’import { CheckboxPanel } from '../ui-components/checkbox-panel';
 18â†’import { Dropdown } from '@nx/graph/legacy/components';
 19â†’import { useRouteConstructor } from '@nx/graph/legacy/shared';
 20â†’import { useCurrentPath } from '../hooks/use-current-path';
 21â†’import { ShowHideAll } from '../ui-components/show-hide-all';
 22â†’import { useTaskGraphLazyLoading } from '../hooks/use-task-graph-lazy-loading';
 23â†’import { createTaskName } from '../util';
 24â†’
 25â†’export function TasksSidebar() {
 26â†’  const graphService = getGraphService();
 27â†’  const navigate = useNavigate();
 28â†’  const params = useParams();
 29â†’  const createRoute = useRouteConstructor();
 30â†’
 31â†’  const [searchParams, setSearchParams] = useSearchParams();
 32â†’  const groupByProject = searchParams.get('groupByProject') === 'true';
 33â†’
 34â†’  const selectedWorkspaceRouteData = useRouteLoaderData(
 35â†’    'selectedWorkspace'
 36â†’  ) as ProjectGraphClientResponse & { targets: string[] };
 37â†’  const workspaceLayout = selectedWorkspaceRouteData.layout;
 38â†’
 39â†’  const routeData = useRouteLoaderData(
 40â†’    'selectedTarget'
 41â†’  ) as TaskGraphClientResponse;
 42â†’
 43â†’  // Use the new lazy loading hook
 44â†’  const {
 45â†’    taskGraphs: lazyTaskGraphs,
 46â†’    loadTaskGraph,
 47â†’    errors: lazyErrors,
 48â†’    metadata,
 49â†’    hasTaskGraph,
 50â†’    isTaskLoading,
 51â†’  } = useTaskGraphLazyLoading(params.selectedWorkspaceId || '', routeData);
 52â†’
 53â†’  // Merge legacy and lazy loaded data
 54â†’  const taskGraphs = { ...(routeData.taskGraphs || {}), ...lazyTaskGraphs };
 55â†’  const errors = { ...(routeData.errors || {}), ...lazyErrors };
 56â†’
 57â†’  let { projects, targets } = selectedWorkspaceRouteData;
 58â†’
 59â†’  // If we have metadata, use it to build the targets list more efficiently
 60â†’  if (metadata) {
 61â†’    const allTargets = new Set<string>();
 62â†’    metadata.projects.forEach((project) => {
 63â†’      project.targets.forEach((target) => {
 64â†’        allTargets.add(target.name);
 65â†’      });
 66â†’    });
 67â†’    targets = Array.from(allTargets).sort();
 68â†’  }
 69â†’
 70â†’  const selectedTarget = params['selectedTarget'] ?? targets[0];
 71â†’
 72â†’  const currentRoute = useCurrentPath();
 73â†’  const isAllRoute =
 74â†’    currentRoute.currentPath === `/tasks/${selectedTarget}/all`;
 75â†’
 76â†’  const allProjectsWithTargetAndNoErrors = projects.filter(
 77â†’    (project) =>
 78â†’      project.data.targets?.hasOwnProperty(selectedTarget) &&
 79â†’      !errors?.hasOwnProperty(createTaskName(project.name, selectedTarget))
 80â†’  );
 81â†’
 82â†’  const selectedProjects = useMemo(
 83â†’    () =>
 84â†’      isAllRoute
 85â†’        ? allProjectsWithTargetAndNoErrors.map(({ name }) => name)
 86â†’        : searchParams.get('projects')?.split(' ') ?? [],
 87â†’    [allProjectsWithTargetAndNoErrors, searchParams, isAllRoute]
 88â†’  );
 89â†’
 90â†’  function selectTarget(target: string) {
 91â†’    if (target === selectedTarget) return;
 92â†’
 93â†’    hideAllProjects();
 94â†’
 95â†’    if (params['selectedTarget']) {
 96â†’      navigate({
 97â†’        pathname: `../${encodeURIComponent(target)}`,
 98â†’        search: searchParams.toString(),
 99â†’      });
100â†’    } else {
101â†’      navigate({
102â†’        pathname: `${encodeURIComponent(target)}`,
103â†’        search: searchParams.toString(),
104â†’      });
105â†’    }
106â†’  }
107â†’
108â†’  function toggleProject(project: string) {
109â†’    if (selectedProjects.includes(project)) {
110â†’      deselectProject(project);
111â†’    } else {
112â†’      selectProject(project);
113â†’    }
114â†’  }
115â†’
116â†’  function selectProject(project: string) {
117â†’    const newSelectedProjects = [...selectedProjects, project];
118â†’    const allProjectsSelected =
119â†’      newSelectedProjects.length === allProjectsWithTargetAndNoErrors.length;
120â†’    if (allProjectsSelected) {
