261â†’      handleGetFilesInDirectory(payload.dir)
262â†’    );
263â†’  } else if (isHandleContextFileDataMessage(payload)) {
264â†’    await handleResult(socket, GET_CONTEXT_FILE_DATA, () =>
265â†’      handleContextFileData()
266â†’    );
267â†’  } else if (isHandleHashGlobMessage(payload)) {
268â†’    await handleResult(socket, HASH_GLOB, () =>
269â†’      handleHashGlob(payload.globs, payload.exclude)
270â†’    );
271â†’  } else if (isHandleHashMultiGlobMessage(payload)) {
272â†’    await handleResult(socket, HASH_GLOB, () =>
273â†’      handleHashMultiGlob(payload.globGroups)
274â†’    );
275â†’  } else if (isHandleGetFlakyTasksMessage(payload)) {
276â†’    await handleResult(socket, GET_FLAKY_TASKS, () =>
277â†’      handleGetFlakyTasks(payload.hashes)
278â†’    );
279â†’  } else if (isHandleGetEstimatedTaskTimings(payload)) {
280â†’    await handleResult(socket, GET_ESTIMATED_TASK_TIMINGS, () =>
281â†’      handleGetEstimatedTaskTimings(payload.targets)
282â†’    );
283â†’  } else if (isHandleWriteTaskRunsToHistoryMessage(payload)) {
284â†’    await handleResult(socket, RECORD_TASK_RUNS, () =>
285â†’      handleRecordTaskRuns(payload.taskRuns)
286â†’    );
287â†’  } else if (isHandleForceShutdownMessage(payload)) {
288â†’    await handleResult(socket, 'FORCE_SHUTDOWN', () =>
289â†’      handleForceShutdown(server)
290â†’    );
291â†’  } else if (isHandleGetSyncGeneratorChangesMessage(payload)) {
292â†’    await handleResult(socket, GET_SYNC_GENERATOR_CHANGES, () =>
293â†’      handleGetSyncGeneratorChanges(payload.generators)
294â†’    );
295â†’  } else if (isHandleFlushSyncGeneratorChangesToDiskMessage(payload)) {
296â†’    await handleResult(socket, FLUSH_SYNC_GENERATOR_CHANGES_TO_DISK, () =>
297â†’      handleFlushSyncGeneratorChangesToDisk(payload.generators)
298â†’    );
299â†’  } else if (isHandleGetRegisteredSyncGeneratorsMessage(payload)) {
300â†’    await handleResult(socket, GET_REGISTERED_SYNC_GENERATORS, () =>
301â†’      handleGetRegisteredSyncGenerators()
302â†’    );
303â†’  } else if (isHandleUpdateWorkspaceContextMessage(payload)) {
304â†’    await handleResult(socket, UPDATE_WORKSPACE_CONTEXT, () =>
305â†’      handleUpdateWorkspaceContext(
306â†’        payload.createdFiles,
307â†’        payload.updatedFiles,
308â†’        payload.deletedFiles
309â†’      )
310â†’    );
311â†’  } else if (isHandlePreTasksExecutionMessage(payload)) {
312â†’    await handleResult(socket, PRE_TASKS_EXECUTION, () =>
313â†’      handleRunPreTasksExecution(payload.context)
314â†’    );
315â†’  } else if (isHandlePostTasksExecutionMessage(payload)) {
316â†’    await handleResult(socket, POST_TASKS_EXECUTION, () =>
317â†’      handleRunPostTasksExecution(payload.context)
318â†’    );
319â†’  } else {
320â†’    await respondWithErrorAndExit(
321â†’      socket,
322â†’      `Invalid payload from the client`,
323â†’      new Error(`Unsupported payload sent to daemon server: ${unparsedPayload}`)
324â†’    );
325â†’  }
326â†’}
327â†’
328â†’export async function handleResult(
329â†’  socket: Socket,
330â†’  type: string,
331â†’  hrFn: () => Promise<HandlerResult>
332â†’) {
333â†’  const startMark = new Date();
334â†’  const hr = await hrFn();
335â†’  const doneHandlingMark = new Date();
336â†’  if (hr.error) {
337â†’    await respondWithErrorAndExit(socket, hr.description, hr.error);
338â†’  } else {
339â†’    await respondToClient(socket, hr.response, hr.description);
340â†’  }
341â†’  const endMark = new Date();
342â†’  serverLogger.log(
343â†’    `Handled ${type}. Handling time: ${
344â†’      doneHandlingMark.getTime() - startMark.getTime()
345â†’    }. Response time: ${endMark.getTime() - doneHandlingMark.getTime()}.`
346â†’  );
347â†’}
348â†’
349â†’function handleInactivityTimeout() {
350â†’  if (hasRegisteredFileWatcherSockets()) {
351â†’    serverLogger.log(
352â†’      `There are open file watchers. Resetting inactivity timer.`
353â†’    );
354â†’    resetInactivityTimeout(handleInactivityTimeout);
355â†’  } else {
356â†’    handleServerProcessTermination({
357â†’      server,
358â†’      reason: `${SERVER_INACTIVITY_TIMEOUT_MS}ms of inactivity`,
359â†’      sockets: openSockets,
360â†’    });
361â†’  }
362â†’}
363â†’
364â†’function registerProcessTerminationListeners() {
365â†’  process
366â†’    .on('SIGINT', () =>
367â†’      handleServerProcessTermination({
368â†’        server,
369â†’        reason: 'received process SIGINT',
370â†’        sockets: openSockets,
371â†’      })
372â†’    )
373â†’    .on('SIGTERM', () =>
374â†’      handleServerProcessTermination({
375â†’        server,
376â†’        reason: 'received process SIGTERM',
377â†’        sockets: openSockets,
378â†’      })
379â†’    )
380â†’    .on('SIGHUP', () =>
381â†’      handleServerProcessTermination({
382â†’        server,
383â†’        reason: 'received process SIGHUP',
384â†’        sockets: openSockets,
385â†’      })
386â†’    );
387â†’}
388â†’
389â†’let existingLockHash: string | undefined;
390â†’
391â†’function daemonIsOutdated(): string | null {
392â†’  if (nxVersionChanged()) {
393â†’    return 'NX_VERSION_CHANGED';
394â†’  } else if (lockFileHashChanged()) {
395â†’    return 'LOCK_FILES_CHANGED';
396â†’  }
397â†’  return null;
398â†’}
399â†’
400â†’function nxVersionChanged(): boolean {
401â†’  return nxVersion !== getInstalledNxVersion();
402â†’}
403â†’
404â†’const nxPackageJsonPath = require.resolve('nx/package.json');
405â†’
406â†’function getInstalledNxVersion() {
407â†’  try {
408â†’    const { version } = readJsonFile<PackageJson>(nxPackageJsonPath);
409â†’    return version;
410â†’  } catch (e) {
411â†’    // node modules are absent, so we can return null, which would shut down the daemon
412â†’    return null;
413â†’  }
414â†’}
415â†’
416â†’function lockFileHashChanged(): boolean {
417â†’  const lockHashes = [
418â†’    join(workspaceRoot, 'package-lock.json'),
419â†’    join(workspaceRoot, 'yarn.lock'),
420â†’    join(workspaceRoot, 'pnpm-lock.yaml'),
421â†’    join(workspaceRoot, 'bun.lockb'),
422â†’    join(workspaceRoot, 'bun.lock'),
423â†’  ]
424â†’    .filter((file) => existsSync(file))
425â†’    .map((file) => hashFile(file));
426â†’  const newHash = hashArray(lockHashes);
427â†’  if (existingLockHash && newHash != existingLockHash) {
428â†’    existingLockHash = newHash;
429â†’    return true;
430â†’  } else {
431â†’    existingLockHash = newHash;
432â†’    return false;
433â†’  }
434â†’}
435â†’
436â†’/**
437â†’ * When applicable files in the workspaces are changed (created, updated, deleted),
438â†’ * we need to recompute the cached serialized project graph so that it is readily
439â†’ * available for the next client request to the server.
440â†’ */
441â†’const handleWorkspaceChanges: FileWatcherCallback = async (
442â†’  err,
443â†’  changeEvents
444â†’) => {
445â†’  if (workspaceWatcherError) {
446â†’    serverLogger.watcherLog(
447â†’      'Skipping handleWorkspaceChanges because of a previously recorded watcher error.'
448â†’    );
449â†’    return;
450â†’  }
451â†’
452â†’  try {
453â†’    resetInactivityTimeout(handleInactivityTimeout);
454â†’
455â†’    const outdatedReason = daemonIsOutdated();
456â†’    if (outdatedReason) {
457â†’      await handleServerProcessTermination({
458â†’        server,
459â†’        reason: outdatedReason,
460â†’        sockets: openSockets,
461â†’      });
462â†’      return;
463â†’    }
464â†’
465â†’    if (err) {
466â†’      let error = typeof err === 'string' ? new Error(err) : err;
467â†’      serverLogger.watcherLog(
468â†’        'Unexpected workspace watcher error',
469â†’        error.message
470â†’      );
471â†’      console.error(error);
472â†’      workspaceWatcherError = error;
473â†’      return;
474â†’    }
475â†’
476â†’    serverLogger.watcherLog(convertChangeEventsToLogMessage(changeEvents));
477â†’
478â†’    const updatedFilesToHash = [];
479â†’    const createdFilesToHash = [];
480â†’    const deletedFiles = [];
481â†’
482â†’    for (const event of changeEvents) {
483â†’      if (event.type === 'delete') {
484â†’        deletedFiles.push(event.path);
485â†’      } else {
486â†’        try {
487â†’          const s = statSync(join(workspaceRoot, event.path));
488â†’          if (s.isFile()) {
489â†’            if (event.type === 'update') {
490â†’              updatedFilesToHash.push(event.path);
491â†’            } else {
492â†’              createdFilesToHash.push(event.path);
493â†’            }
494â†’          }
495â†’        } catch (e) {
496â†’          // this can happen when the update file was deleted right after
497â†’        }
498â†’      }
499â†’    }
500â†’
501â†’    addUpdatedAndDeletedFiles(
502â†’      createdFilesToHash,
503â†’      updatedFilesToHash,
504â†’      deletedFiles
505â†’    );
506â†’  } catch (err) {
507â†’    serverLogger.watcherLog(`Unexpected workspace error`, err.message);
508â†’    console.error(err);
509â†’    workspaceWatcherError = err;
510â†’  }
511â†’};
512â†’
513â†’const handleOutputsChanges: FileWatcherCallback = async (err, changeEvents) => {
514â†’  try {
515â†’    if (err || !changeEvents || !changeEvents.length) {
516â†’      let error = typeof err === 'string' ? new Error(err) : err;
517â†’      serverLogger.watcherLog(
518â†’        'Unexpected outputs watcher error',
519â†’        error.message
520â†’      );
521â†’      console.error(error);
522â†’      outputsWatcherError = error;
523â†’      disableOutputsTracking();
524â†’      return;
525â†’    }
526â†’    if (outputsWatcherError) {
527â†’      return;
528â†’    }
529â†’
530â†’    serverLogger.watcherLog('Processing file changes in outputs');
531â†’    processFileChangesInOutputs(changeEvents);
532â†’  } catch (err) {
533â†’    serverLogger.watcherLog(`Unexpected outputs watcher error`, err.message);
534â†’    console.error(err);
535â†’    outputsWatcherError = err;
536â†’    disableOutputsTracking();
537â†’  }
538â†’};
539â†’
540â†’export async function startServer(): Promise<Server> {
541â†’  setupWorkspaceContext(workspaceRoot);
542â†’
543â†’  // Persist metadata about the background process so that it can be cleaned up later if needed
544â†’  await writeDaemonJsonProcessCache({
545â†’    processId: process.pid,
546â†’  });
547â†’
548â†’  // See notes in socket-command-line-utils.ts on OS differences regarding clean up of existings connections.
549â†’  if (!isWindows) {
550â†’    killSocketOrPath();
551â†’  }
552â†’
553â†’  return new Promise(async (resolve, reject) => {
554â†’    try {
555â†’      server.listen(getFullOsSocketPath(), async () => {
556â†’        try {
557â†’          serverLogger.log(`Started listening on: ${getFullOsSocketPath()}`);
558â†’
559â†’          setInterval(() => {
560â†’            if (getDaemonProcessIdSync() !== process.pid) {
561â†’              return handleServerProcessTermination({
562â†’                server,
563â†’                reason: 'this process is no longer the current daemon (native)',
564â†’                sockets: openSockets,
565â†’              });
566â†’            }
567â†’          }).unref();
568â†’
569â†’          // this triggers the storage of the lock file hash
570â†’          daemonIsOutdated();
571â†’
572â†’          if (!getWatcherInstance()) {
573â†’            storeWatcherInstance(
574â†’              await watchWorkspace(server, handleWorkspaceChanges)
575â†’            );
576â†’
577â†’            serverLogger.watcherLog(
578â†’              `Subscribed to changes within: ${workspaceRoot} (native)`
579â†’            );
580â†’          }
581â†’
582â†’          if (!getOutputWatcherInstance()) {
583â†’            storeOutputWatcherInstance(
584â†’              await watchOutputFiles(server, handleOutputsChanges)
585â†’            );
586â†’          }
587â†’
588â†’          // listen for project graph recomputation events to collect and schedule sync generators
589â†’          registerProjectGraphRecomputationListener(
590â†’            collectAndScheduleSyncGenerators
591â†’          );
592â†’          // trigger an initial project graph recomputation
593â†’          addUpdatedAndDeletedFiles([], [], []);
594â†’
595â†’          return resolve(server);
596â†’        } catch (err) {
597â†’          await handleWorkspaceChanges(err, []);
598â†’        }
599â†’      });
600â†’    } catch (err) {
601â†’      reject(err);
602â†’    }
603â†’  });
604â†’}
605â†’

