 412â†’    const workspaceFolder = workspaceRoot;
 413â†’    const ext = extname(args.file);
 414â†’    const fullFilePath = isAbsolute(args.file)
 415â†’      ? args.file
 416â†’      : join(workspaceFolder, args.file);
 417â†’    const fileFolderPath = dirname(fullFilePath);
 418â†’
 419â†’    if (ext === '.html') {
