 481â†’        title: `Please specify a filename with either .json or .html extension.`,
 482â†’        bodyLines: [`You provided --file=${args.file}`],
 483â†’      });
 484â†’      process.exit(1);
 485â†’    }
 486â†’    process.exit(0);
 487â†’  } else {
 488â†’    const environmentJs = buildEnvironmentJs(
 489â†’      args.exclude || [],
 490â†’      args.watch,
 491â†’      !!args.file && args.file.endsWith('html') ? 'build' : 'serve'
 492â†’    );
 493â†’
 494â†’    let app: Server;
 495â†’    let url: URL;
 496â†’    try {
 497â†’      const result = await startServer(
 498â†’        html,
 499â†’        environmentJs,
 500â†’        args.host || '127.0.0.1',
 501â†’        args.port || 4211,
 502â†’        args.watch,
 503â†’        affectedProjects,
 504â†’        args.focus,
 505â†’        args.groupByFolder,
 506â†’        args.exclude
 507â†’      );
 508â†’      app = result.app;
 509â†’      url = result.url;
 510â†’    } catch (err) {
 511â†’      output.error({
 512â†’        title: 'Failed to start graph server',
 513â†’        bodyLines: [err.message],
 514â†’      });
 515â†’      process.exit(1);
 516â†’    }
 517â†’
 518â†’    url.pathname = args.view;
 519â†’
 520â†’    if (args.focus) {
 521â†’      url.pathname += '/' + encodeURIComponent(args.focus);
 522â†’    }
 523â†’
 524â†’    if (target) {
