 793â†’function createFileWatcher() {
 794â†’  return daemonClient.registerFileWatcher(
 795â†’    {
 796â†’      watchProjects: 'all',
 797â†’      includeGlobalWorkspaceFiles: true,
 798â†’      allowPartialGraph: true,
 799â†’    },
 800â†’    debounce(async (error, changes) => {
 801â†’      if (error === 'closed') {
 802â†’        output.error({ title: `Watch error: Daemon closed the connection` });
 803â†’        process.exit(1);
 804â†’      } else if (error) {
 805â†’        output.error({ title: `Watch error: ${error?.message ?? 'Unknown'}` });
 806â†’      } else if (changes !== null && changes.changedFiles.length > 0) {
 807â†’        output.note({ title: 'Recalculating project graph...' });
 808â†’
 809â†’        const { projectGraphClientResponse, sourceMapResponse } =
 810â†’          await createProjectGraphAndSourceMapClientResponse();
 811â†’
 812â†’        if (
 813â†’          projectGraphClientResponse.hash !==
 814â†’            currentProjectGraphClientResponse.hash &&
 815â†’          sourceMapResponse
 816â†’        ) {
 817â†’          if (projectGraphClientResponse.errors?.length > 0) {
 818â†’            projectGraphClientResponse.errors.forEach((e) => {
 819â†’              output.error({
 820â†’                title: e.message,
 821â†’                bodyLines: [e.stack],
 822â†’              });
 823â†’            });
 824â†’            output.warn({
 825â†’              title: `${
 826â†’                projectGraphClientResponse.errors.length > 1
 827â†’                  ? `${projectGraphClientResponse.errors.length} errors`
 828â†’                  : `An error`
 829â†’              } occured while processing the project graph. Showing partial graph.`,
 830â†’            });
 831â†’          }
 832â†’          output.note({ title: 'Graph changes updated.' });
 833â†’
 834â†’          currentProjectGraphClientResponse = projectGraphClientResponse;
 835â†’          currentSourceMapsClientResponse = sourceMapResponse;
 836â†’          // Clear task graph cache when project graph changes
 837â†’          clearTaskGraphCache();
 838â†’        } else {
 839â†’          output.note({ title: 'No graph changes found.' });
 840â†’        }
 841â†’      }
 842â†’    }, 500)
 843â†’  );
 844â†’}
