 846â†’async function createProjectGraphAndSourceMapClientResponse(
 847â†’  affected: string[] = []
 848â†’): Promise<{
 849â†’  projectGraphClientResponse: ProjectGraphClientResponse;
 850â†’  sourceMapResponse: ConfigurationSourceMaps;
 851â†’}> {
 852â†’  performance.mark('project graph watch calculation:start');
 853â†’
 854â†’  let projectGraph: ProjectGraph;
 855â†’  let sourceMaps: ConfigurationSourceMaps;
 856â†’  let isPartial = false;
 857â†’  let errors: GraphError[] | undefined;
 858â†’  let connectedToCloud: boolean | undefined;
 859â†’  let disabledTaskSyncGenerators: string[] | undefined;
 860â†’  try {
 861â†’    const projectGraphAndSourceMaps =
 862â†’      await createProjectGraphAndSourceMapsAsync({ exitOnError: false });
 863â†’    projectGraph = projectGraphAndSourceMaps.projectGraph;
 864â†’    sourceMaps = projectGraphAndSourceMaps.sourceMaps;
 865â†’
 866â†’    const nxJson = readNxJson();
 867â†’    connectedToCloud = isNxCloudUsed(nxJson);
 868â†’    disabledTaskSyncGenerators = nxJson.sync?.disabledTaskSyncGenerators;
 869â†’  } catch (e) {
 870â†’    if (e instanceof ProjectGraphError) {
 871â†’      projectGraph = e.getPartialProjectGraph();
 872â†’      sourceMaps = e.getPartialSourcemaps();
 873â†’      errors = e.getErrors().map((e) => ({
 874â†’        message: e.message,
 875â†’        stack: e.stack,
 876â†’        cause: e.cause,
 877â†’        name: e.name,
 878â†’        pluginName: (e as any).pluginName,
 879â†’        fileName:
 880â†’          (e as any).file ?? (e.cause as any)?.errors?.[0]?.location?.file,
 881â†’      }));
 882â†’      isPartial = true;
 883â†’    }
 884â†’
 885â†’    if (!projectGraph) {
 886â†’      handleProjectGraphError({ exitOnError: true }, e);
 887â†’    }
 888â†’  }
 889â†’
 890â†’  let graph = pruneExternalNodes(projectGraph);
 891â†’  let fileMap: ProjectFileMap | undefined =
 892â†’    readFileMapCache()?.fileMap.projectFileMap;
 893â†’  performance.mark('project graph watch calculation:end');
 894â†’  performance.mark('project graph response generation:start');
 895â†’
 896â†’  const layout = workspaceLayout();
 897â†’  const projects: ProjectGraphProjectNode[] = Object.values(graph.nodes);
 898â†’  const dependencies = graph.dependencies;
 899â†’
 900â†’  const hasher = createHash('sha256');
 901â†’  hasher.update(
 902â†’    JSON.stringify({
 903â†’      layout,
 904â†’      projects,
 905â†’      dependencies,
 906â†’      sourceMaps,
 907â†’      errors,
 908â†’      connectedToCloud,
 909â†’      disabledTaskSyncGenerators,
 910â†’    })
 911â†’  );
 912â†’
 913â†’  const hash = hasher.digest('hex');
 914â†’
 915â†’  performance.mark('project graph response generation:end');
 916â†’
 917â†’  performance.measure(
 918â†’    'project graph watch calculation',
 919â†’    'project graph watch calculation:start',
 920â†’    'project graph watch calculation:end'
 921â†’  );
 922â†’
 923â†’  performance.measure(
 924â†’    'project graph response generation',
 925â†’    'project graph response generation:start',
 926â†’    'project graph response generation:end'
 927â†’  );
 928â†’
 929â†’  return {
 930â†’    projectGraphClientResponse: {
 931â†’      ...currentProjectGraphClientResponse,
 932â†’      hash,
 933â†’      layout,
 934â†’      projects,
 935â†’      dependencies,
 936â†’      affected,
 937â†’      fileMap,
 938â†’      isPartial,
 939â†’      errors,
 940â†’      connectedToCloud,
 941â†’      disabledTaskSyncGenerators,
 942â†’    },
 943â†’    sourceMapResponse: sourceMaps,
 944â†’  };
 945â†’}
