1421â†’  for (const pattern of positiveWRPatterns) {
1422â†’    const matchingFile = allWorkspaceFiles.find((t) => t.file === pattern);
1423â†’    if (
1424â†’      matchingFile &&
1425â†’      !negativeWRPatterns.some((p) => minimatch(matchingFile.file, p))
1426â†’    ) {
1427â†’      workspaceRootsExpanded.push(matchingFile.file);
1428â†’    } else {
1429â†’      allWorkspaceFiles
1430â†’        .filter(
1431â†’          (f) =>
1432â†’            minimatch(f.file, pattern) &&
1433â†’            !negativeWRPatterns.some((p) => minimatch(f.file, p))
