 305â†’        process.stderr.write = originalStderrWrite;
 306â†’        console.log = originalConsoleLog;
 307â†’        console.error = originalConsoleError;
 308â†’      });
 309â†’    }
 310â†’
 311â†’    return {
 312â†’      lifeCycle: new CompositeLifeCycle(lifeCycles),
 313â†’      restoreTerminal: () => {
 314â†’        process.stdout.write = originalStdoutWrite;
 315â†’        process.stderr.write = originalStderrWrite;
 316â†’        console.log = originalConsoleLog;
 317â†’        console.error = originalConsoleError;
 318â†’        restoreTerminal();
 319â†’      },
 320â†’      printSummary,
 321â†’      renderIsDone,
 322â†’    };
 323â†’  }
 324â†’
 325â†’  const { runnerOptions } = getRunner(nxArgs, nxJson);
 326â†’  const useDynamicOutput = shouldUseDynamicLifeCycle(
 327â†’    tasks,
 328â†’    runnerOptions,
 329â†’    nxArgs.outputStyle
 330â†’  );
 331â†’
 332â†’  if (isRunOne) {
 333â†’    if (useDynamicOutput) {
 334â†’      return await createRunOneDynamicOutputRenderer({
 335â†’        initiatingProject,
 336â†’        tasks,
 337â†’        args: nxArgs,
 338â†’        overrides: overridesWithoutHidden,
 339â†’      });
 340â†’    }
 341â†’    return {
 342â†’      lifeCycle: new StaticRunOneTerminalOutputLifeCycle(
 343â†’        initiatingProject,
 344â†’        projectNames,
 345â†’        tasks,
 346â†’        nxArgs
 347â†’      ),
 348â†’      renderIsDone: Promise.resolve(),
 349â†’    };
 350â†’  } else {
 351â†’    if (useDynamicOutput) {
 352â†’      return await createRunManyDynamicOutputRenderer({
 353â†’        projectNames,
 354â†’        tasks,
 355â†’        args: nxArgs,
 356â†’        overrides: overridesWithoutHidden,
 357â†’      });
 358â†’    } else {
 359â†’      return {
 360â†’        lifeCycle: new StaticRunManyTerminalOutputLifeCycle(
 361â†’          projectNames,
 362â†’          tasks,
 363â†’          nxArgs,
 364â†’          overridesWithoutHidden
 365â†’        ),
 366â†’        renderIsDone: Promise.resolve(),
 367â†’      };
 368â†’    }
 369â†’  }
 370â†’}
