 372â†’function createTaskGraphAndRunValidations(
 373â†’  projectGraph: ProjectGraph,
 374â†’  extraTargetDependencies: TargetDependencies,
 375â†’  projectNames: string[],
 376â†’  nxArgs: NxArgs,
 377â†’  overrides: any,
 378â†’  extraOptions: {
 379â†’    excludeTaskDependencies: boolean;
 380â†’    loadDotEnvFiles: boolean;
 381â†’  }
 382â†’) {
 383â†’  const taskGraph = createTaskGraph(
 384â†’    projectGraph,
 385â†’    extraTargetDependencies,
 386â†’    projectNames,
 387â†’    nxArgs.targets,
 388â†’    nxArgs.configuration,
 389â†’    overrides,
 390â†’    extraOptions.excludeTaskDependencies
 391â†’  );
 392â†’
 393â†’  assertTaskGraphDoesNotContainInvalidTargets(taskGraph);
 394â†’
 395â†’  const cycle = findCycle(taskGraph);
 396â†’  if (cycle) {
 397â†’    if (process.env.NX_IGNORE_CYCLES === 'true' || nxArgs.nxIgnoreCycles) {
 398â†’      output.warn({
 399â†’        title: `The task graph has a circular dependency`,
 400â†’        bodyLines: [`${cycle.join(' --> ')}`],
 401â†’      });
 402â†’      makeAcyclic(taskGraph);
 403â†’    } else {
 404â†’      output.error({
 405â†’        title: `Could not execute command because the task graph has a circular dependency`,
 406â†’        bodyLines: [`${cycle.join(' --> ')}`],
 407â†’      });
 408â†’      process.exit(1);
 409â†’    }
 410â†’  }
 411â†’
 412â†’  // validate that no atomized tasks like e2e-ci are used without Nx Cloud
 413â†’  if (
 414â†’    !isNxCloudUsed(readNxJson()) &&
 415â†’    !process.env['NX_SKIP_ATOMIZER_VALIDATION']
 416â†’  ) {
 417â†’    validateNoAtomizedTasks(taskGraph, projectGraph);
 418â†’  }
 419â†’
 420â†’  return taskGraph;
 421â†’}
