 423â†’export async function runCommand(
 424â†’  projectsToRun: ProjectGraphProjectNode[],
 425â†’  currentProjectGraph: ProjectGraph,
 426â†’  { nxJson }: { nxJson: NxJsonConfiguration },
 427â†’  nxArgs: NxArgs,
 428â†’  overrides: any,
 429â†’  initiatingProject: string | null,
 430â†’  extraTargetDependencies: Record<string, (TargetDependencyConfig | string)[]>,
 431â†’  extraOptions: { excludeTaskDependencies: boolean; loadDotEnvFiles: boolean }
 432â†’): Promise<NodeJS.Process['exitCode']> {
 433â†’  const status = await handleErrors(
 434â†’    process.env.NX_VERBOSE_LOGGING === 'true',
 435â†’    async () => {
 436â†’      await runPreTasksExecution({
 437â†’        workspaceRoot,
 438â†’        nxJsonConfiguration: nxJson,
 439â†’      });
 440â†’
 441â†’      const { taskResults, completed } = await runCommandForTasks(
 442â†’        projectsToRun,
 443â†’        currentProjectGraph,
 444â†’        { nxJson },
 445â†’        {
 446â†’          ...nxArgs,
 447â†’          skipNxCache:
 448â†’            nxArgs.skipNxCache ||
 449â†’            process.env.NX_SKIP_NX_CACHE === 'true' ||
 450â†’            process.env.NX_DISABLE_NX_CACHE === 'true',
 451â†’        },
 452â†’        overrides,
 453â†’        initiatingProject,
 454â†’        extraTargetDependencies,
 455â†’        extraOptions
 456â†’      );
 457â†’
 458â†’      const exitCode = !completed
 459â†’        ? signalToCode('SIGINT')
 460â†’        : Object.values(taskResults).some(
 461â†’            (taskResult) =>
 462â†’              taskResult.status === 'failure' || taskResult.status === 'skipped'
 463â†’          )
 464â†’        ? 1
 465â†’        : 0;
 466â†’
 467â†’      await runPostTasksExecution({
 468â†’        taskResults,
 469â†’        workspaceRoot,
 470â†’        nxJsonConfiguration: nxJson,
 471â†’      });
 472â†’
 473â†’      return exitCode;
 474â†’    }
 475â†’  );
 476â†’
 477â†’  return status;
 478â†’}
