 480â†’export async function runCommandForTasks(
 481â†’  projectsToRun: ProjectGraphProjectNode[],
 482â†’  currentProjectGraph: ProjectGraph,
 483â†’  { nxJson }: { nxJson: NxJsonConfiguration },
 484â†’  nxArgs: NxArgs,
 485â†’  overrides: any,
 486â†’  initiatingProject: string | null,
 487â†’  extraTargetDependencies: Record<string, (TargetDependencyConfig | string)[]>,
 488â†’  extraOptions: { excludeTaskDependencies: boolean; loadDotEnvFiles: boolean }
 489â†’): Promise<{ taskResults: TaskResults; completed: boolean }> {
 490â†’  const projectNames = projectsToRun.map((t) => t.name);
 491â†’  const projectNameSet = new Set(projectNames);
 492â†’
 493â†’  const { projectGraph, taskGraph } = await ensureWorkspaceIsInSyncAndGetGraphs(
 494â†’    currentProjectGraph,
 495â†’    nxJson,
 496â†’    projectNames,
 497â†’    nxArgs,
 498â†’    overrides,
 499â†’    extraTargetDependencies,
 500â†’    extraOptions
 501â†’  );
 502â†’  const tasks = Object.values(taskGraph.tasks);
 503â†’
 504â†’  const initiatingTasks = tasks.filter(
 505â†’    (t) =>
 506â†’      projectNameSet.has(t.target.project) &&
 507â†’      nxArgs.targets.includes(t.target.target)
 508â†’  );
 509â†’
 510â†’  const { lifeCycle, renderIsDone, printSummary, restoreTerminal } =
 511â†’    await getTerminalOutputLifeCycle(
 512â†’      initiatingProject,
 513â†’      initiatingTasks,
 514â†’      projectNames,
 515â†’      tasks,
 516â†’      taskGraph,
 517â†’      nxArgs,
 518â†’      nxJson,
 519â†’      overrides
 520â†’    );
 521â†’
 522â†’  try {
 523â†’    const taskResults = await invokeTasksRunner({
 524â†’      tasks,
 525â†’      projectGraph,
 526â†’      taskGraph,
 527â†’      lifeCycle,
 528â†’      nxJson,
 529â†’      nxArgs,
 530â†’      loadDotEnvFiles: extraOptions.loadDotEnvFiles,
 531â†’      initiatingProject,
 532â†’      initiatingTasks,
 533â†’    });
 534â†’
 535â†’    await renderIsDone;
 536â†’
 537â†’    if (printSummary) {
 538â†’      printSummary();
 539â†’    }
 540â†’
 541â†’    await printNxKey();
 542â†’
 543â†’    return {
 544â†’      taskResults,
 545â†’      completed: didCommandComplete(tasks, taskGraph, taskResults),
 546â†’    };
 547â†’  } catch (e) {
 548â†’    if (restoreTerminal) {
 549â†’      restoreTerminal();
 550â†’    }
 551â†’    throw e;
 552â†’  }
 553â†’}
 554â†’
 555â†’function didCommandComplete(
 556â†’  tasks: Task[],
 557â†’  taskGraph: TaskGraph,
 558â†’  taskResults: TaskResults
 559â†’): boolean {
 560â†’  // If no tasks, then we can consider it complete
 561â†’  if (tasks.length === 0) {
 562â†’    return true;
 563â†’  }
 564â†’
 565â†’  let continousLeafTasks = false;
 566â†’  const leafTasks = getLeafTasks(taskGraph);
 567â†’  for (const task of tasks) {
 568â†’    if (!task.continuous) {
 569â†’      // If any discrete task does not have a result then it did not run
 570â†’      if (!taskResults[task.id]) {
 571â†’        return false;
 572â†’      }
 573â†’    } else {
 574â†’      if (leafTasks.has(task.id)) {
 575â†’        continousLeafTasks = true;
 576â†’      }
 577â†’    }
 578â†’  }
 579â†’
 580â†’  // If a leaf task is continous, we must have cancelled it.
 581â†’  // Otherwise, we've looped through all the discrete tasks and they have results
 582â†’  return !continousLeafTasks;
 583â†’}
 584â†’
 585â†’async function ensureWorkspaceIsInSyncAndGetGraphs(
 586â†’  projectGraph: ProjectGraph,
 587â†’  nxJson: NxJsonConfiguration,
 588â†’  projectNames: string[],
 589â†’  nxArgs: NxArgs,
 590â†’  overrides: any,
 591â†’  extraTargetDependencies: Record<string, (TargetDependencyConfig | string)[]>,
 592â†’  extraOptions: { excludeTaskDependencies: boolean; loadDotEnvFiles: boolean }
 593â†’): Promise<{
 594â†’  projectGraph: ProjectGraph;
 595â†’  taskGraph: TaskGraph;
 596â†’}> {
 597â†’  let taskGraph = createTaskGraphAndRunValidations(
 598â†’    projectGraph,
 599â†’    extraTargetDependencies ?? {},
 600â†’    projectNames,
 601â†’    nxArgs,
 602â†’    overrides,
 603â†’    extraOptions
 604â†’  );
 605â†’
 606â†’  if (nxArgs.skipSync || isCI()) {
 607â†’    return { projectGraph, taskGraph };
 608â†’  }
 609â†’
 610â†’  // collect unique syncGenerators from the tasks
 611â†’  const uniqueSyncGenerators = collectEnabledTaskSyncGeneratorsFromTaskGraph(
 612â†’    taskGraph,
 613â†’    projectGraph,
 614â†’    nxJson
 615â†’  );
 616â†’
 617â†’  if (!uniqueSyncGenerators.size) {
 618â†’    // There are no sync generators registered in the tasks to run
 619â†’    return { projectGraph, taskGraph };
 620â†’  }
 621â†’
 622â†’  const syncGenerators = Array.from(uniqueSyncGenerators);
 623â†’  const results = await getSyncGeneratorChanges(syncGenerators);
 624â†’  if (!results.length) {
 625â†’    // There are no changes to sync, workspace is up to date
 626â†’    return { projectGraph, taskGraph };
 627â†’  }
 628â†’
 629â†’  const {
 630â†’    failedGeneratorsCount,
 631â†’    areAllResultsFailures,
 632â†’    anySyncGeneratorsFailed,
 633â†’  } = processSyncGeneratorResultErrors(results);
 634â†’  const failedSyncGeneratorsFixMessageLines =
 635â†’    getFailedSyncGeneratorsFixMessageLines(results, nxArgs.verbose);
 636â†’  const outOfSyncTitle = 'The workspace is out of sync';
 637â†’  const resultBodyLines = getSyncGeneratorSuccessResultsMessageLines(results);
 638â†’  const fixMessage =
 639â†’    'Make sure to run `nx sync` to apply the identified changes or set `sync.applyChanges` to `true` in your `nx.json` to apply them automatically when running tasks in interactive environments.';
 640â†’
 641â†’  if (!process.stdout.isTTY) {
 642â†’    // If the user is running a non-TTY environment we
 643â†’    // throw an error to stop the execution of the tasks.
 644â†’    if (areAllResultsFailures) {
 645â†’      output.error({
 646â†’        title: `The workspace is probably out of sync because ${
 647â†’          failedGeneratorsCount === 1
 648â†’            ? 'a sync generator'
 649â†’            : 'some sync generators'
 650â†’        } failed to run`,
 651â†’        bodyLines: failedSyncGeneratorsFixMessageLines,
 652â†’      });
 653â†’    } else {
 654â†’      output.error({
 655â†’        title: outOfSyncTitle,
 656â†’        bodyLines: [...resultBodyLines, '', fixMessage],
 657â†’      });
 658â†’
 659â†’      if (anySyncGeneratorsFailed) {
 660â†’        output.error({
 661â†’          title:
 662â†’            failedGeneratorsCount === 1
 663â†’              ? 'A sync generator failed to run'
 664â†’              : 'Some sync generators failed to run',
 665â†’          bodyLines: failedSyncGeneratorsFixMessageLines,
 666â†’        });
 667â†’      }
 668â†’    }
 669â†’
 670â†’    process.exit(1);
 671â†’  }
 672â†’
 673â†’  if (areAllResultsFailures) {
 674â†’    output.warn({
 675â†’      title: `The workspace is probably out of sync because ${
 676â†’        failedGeneratorsCount === 1
 677â†’          ? 'a sync generator'
 678â†’          : 'some sync generators'
 679â†’      } failed to run`,
 680â†’      bodyLines: failedSyncGeneratorsFixMessageLines,
 681â†’    });
 682â†’
 683â†’    await confirmRunningTasksWithSyncFailures();
 684â†’
 685â†’    // if all sync generators failed to run there's nothing to sync, we just let the tasks run
 686â†’    return { projectGraph, taskGraph };
 687â†’  }
 688â†’
 689â†’  if (nxJson.sync?.applyChanges === false) {
 690â†’    // If the user has set `sync.applyChanges` to `false` in their `nx.json`
 691â†’    // we don't prompt the them and just log a warning informing them that
 692â†’    // the workspace is out of sync and they have it set to not apply changes
 693â†’    // automatically.
 694â†’    output.warn({
 695â†’      title: outOfSyncTitle,
 696â†’      bodyLines: [
 697â†’        ...resultBodyLines,
 698â†’        '',
 699â†’        'Your workspace is set to not apply the identified changes automatically (`sync.applyChanges` is set to `false` in your `nx.json`).',
 700â†’        fixMessage,
 701â†’      ],
 702â†’    });
 703â†’
 704â†’    if (anySyncGeneratorsFailed) {
 705â†’      output.warn({
 706â†’        title:
 707â†’          failedGeneratorsCount === 1
 708â†’            ? 'A sync generator failed to run'
 709â†’            : 'Some sync generators failed to run',
 710â†’        bodyLines: failedSyncGeneratorsFixMessageLines,
 711â†’      });
 712â†’
 713â†’      await confirmRunningTasksWithSyncFailures();
 714â†’    }
 715â†’
 716â†’    return { projectGraph, taskGraph };
 717â†’  }
 718â†’
 719â†’  output.warn({
 720â†’    title: outOfSyncTitle,
 721â†’    bodyLines: [
 722â†’      ...resultBodyLines,
 723â†’      ...(nxJson.sync?.applyChanges === true
 724â†’        ? [
 725â†’            '',
 726â†’            'Proceeding to sync the identified changes automatically (`sync.applyChanges` is set to `true` in your `nx.json`).',
 727â†’          ]
 728â†’        : []),
 729â†’    ],
 730â†’  });
 731â†’
 732â†’  const applyChanges =
 733â†’    nxJson.sync?.applyChanges === true ||
 734â†’    (await promptForApplyingSyncGeneratorChanges());
 735â†’
 736â†’  if (applyChanges) {
 737â†’    const spinner = ora('Syncing the workspace...');
 738â†’    spinner.start();
 739â†’
 740â†’    // Flush sync generator changes to disk
 741â†’    const flushResult = await flushSyncGeneratorChanges(results);
 742â†’
 743â†’    if ('generatorFailures' in flushResult) {
 744â†’      spinner.fail();
 745â†’      output.error({
 746â†’        title: 'Failed to sync the workspace',
 747â†’        bodyLines: [
 748â†’          ...getFlushFailureMessageLines(flushResult, nxArgs.verbose),
 749â†’          ...(flushResult.generalFailure
 750â†’            ? [
 751â†’                'If needed, you can run the tasks with the `--skip-sync` flag to disable syncing.',
 752â†’              ]
 753â†’            : []),
 754â†’        ],
 755â†’      });
 756â†’
 757â†’      await confirmRunningTasksWithSyncFailures();
 758â†’    }
 759â†’
 760â†’    // Re-create project graph and task graph
 761â†’    projectGraph = await createProjectGraphAsync();
 762â†’    taskGraph = createTaskGraphAndRunValidations(
 763â†’      projectGraph,
 764â†’      extraTargetDependencies ?? {},
 765â†’      projectNames,
 766â†’      nxArgs,
 767â†’      overrides,
 768â†’      extraOptions
 769â†’    );
 770â†’
 771â†’    const successTitle = anySyncGeneratorsFailed
 772â†’      ? // the identified changes were synced successfully, but the workspace
 773â†’        // is still not up to date, which we'll mention next
 774â†’        'The identified changes were synced successfully!'
 775â†’      : // the workspace is fully up to date
 776â†’        'The workspace was synced successfully!';
 777â†’    const successSubtitle =
 778â†’      nxJson.sync?.applyChanges === true
 779â†’        ? 'Please make sure to commit the changes to your repository or this will error in CI.'
 780â†’        : // The user was prompted and we already logged a message about erroring in CI
 781â†’          // so here we just tell them to commit the changes.
 782â†’          'Please make sure to commit the changes to your repository.';
 783â†’    spinner.succeed(`${successTitle}\n\n${successSubtitle}`);
 784â†’
 785â†’    if (anySyncGeneratorsFailed) {
 786â†’      output.warn({
 787â†’        title: `The workspace is probably still out of sync because ${
 788â†’          failedGeneratorsCount === 1
 789â†’            ? 'a sync generator'
 790â†’            : 'some sync generators'
 791â†’        } failed to run`,
 792â†’        bodyLines: failedSyncGeneratorsFixMessageLines,
 793â†’      });
 794â†’
 795â†’      await confirmRunningTasksWithSyncFailures();
 796â†’    }
 797â†’  } else {
 798â†’    if (anySyncGeneratorsFailed) {
 799â†’      output.warn({
 800â†’        title:
 801â†’          failedGeneratorsCount === 1
 802â†’            ? 'A sync generator failed to report the sync status'
 803â†’            : 'Some sync generators failed to report the sync status',
 804â†’        bodyLines: failedSyncGeneratorsFixMessageLines,
 805â†’      });
 806â†’
 807â†’      await confirmRunningTasksWithSyncFailures();
 808â†’    } else {
 809â†’      output.warn({
 810â†’        title: 'Syncing the workspace was skipped',
 811â†’        bodyLines: [
 812â†’          'This could lead to unexpected results or errors when running tasks.',
 813â†’          fixMessage,
 814â†’        ],
 815â†’      });
 816â†’    }
 817â†’  }
 818â†’
 819â†’  return { projectGraph, taskGraph };
 820â†’}
 821â†’
 822â†’async function promptForApplyingSyncGeneratorChanges(): Promise<boolean> {
 823â†’  try {
 824â†’    const promptConfig = {
 825â†’      name: 'applyChanges',
 826â†’      type: 'autocomplete',
 827â†’      message:
 828â†’        'Would you like to sync the identified changes to get your workspace up to date?',
 829â†’      choices: [
 830â†’        {
 831â†’          name: 'yes',
 832â†’          message: 'Yes, sync the changes and run the tasks',
 833â†’        },
 834â†’        {
 835â†’          name: 'no',
 836â†’          message: 'No, run the tasks without syncing the changes',
 837â†’        },
 838â†’      ],
 839â†’      footer: () =>
 840â†’        chalk.dim(
 841â†’          '\nYou can skip this prompt by setting the `sync.applyChanges` option to `true` in your `nx.json`.\nFor more information, refer to the docs: https://nx.dev/concepts/sync-generators.'
 842â†’        ),
 843â†’    };
 844â†’
 845â†’    return await prompt<{ applyChanges: 'yes' | 'no' }>([promptConfig]).then(
 846â†’      ({ applyChanges }) => applyChanges === 'yes'
 847â†’    );
 848â†’  } catch {
 849â†’    process.exit(1);
 850â†’  }
 851â†’}
 852â†’
 853â†’async function confirmRunningTasksWithSyncFailures(): Promise<void> {
 854â†’  try {
 855â†’    const promptConfig = {
 856â†’      name: 'runTasks',
 857â†’      type: 'autocomplete',
 858â†’      message:
 859â†’        'Would you like to ignore the sync failures and continue running the tasks?',
 860â†’      choices: [
 861â†’        {
 862â†’          name: 'yes',
 863â†’          message: 'Yes, ignore the failures and run the tasks',
 864â†’        },
 865â†’        {
 866â†’          name: 'no',
 867â†’          message: `No, don't run the tasks`,
 868â†’        },
 869â†’      ],
 870â†’      footer: () =>
 871â†’        chalk.dim(
 872â†’          `\nWhen running in CI and there are sync failures, the tasks won't run. Addressing the errors above is highly recommended to prevent failures in CI.`
 873â†’        ),
 874â†’    };
 875â†’
 876â†’    const runTasks = await prompt<{ runTasks: 'yes' | 'no' }>([
 877â†’      promptConfig,
 878â†’    ]).then(({ runTasks }) => runTasks === 'yes');
 879â†’
 880â†’    if (!runTasks) {
 881â†’      process.exit(1);
 882â†’    }
 883â†’  } catch {
 884â†’    process.exit(1);
 885â†’  }
 886â†’}
