 908â†’export async function invokeTasksRunner({
 909â†’  tasks,
 910â†’  projectGraph,
 911â†’  taskGraph,
 912â†’  lifeCycle,
 913â†’  nxJson,
 914â†’  nxArgs,
 915â†’  loadDotEnvFiles,
 916â†’  initiatingProject,
 917â†’  initiatingTasks,
 918â†’}: {
 919â†’  tasks: Task[];
 920â†’  projectGraph: ProjectGraph;
 921â†’  taskGraph: TaskGraph;
 922â†’  lifeCycle: LifeCycle;
 923â†’  nxJson: NxJsonConfiguration;
 924â†’  nxArgs: NxArgs;
 925â†’  loadDotEnvFiles: boolean;
 926â†’  initiatingProject: string | null;
 927â†’  initiatingTasks: Task[];
 928â†’}): Promise<{ [id: string]: TaskResult }> {
 929â†’  setEnvVarsBasedOnArgs(nxArgs, loadDotEnvFiles);
 930â†’
 931â†’  // this needs to be done before we start to run the tasks
 932â†’  const taskDetails = getTaskDetails();
 933â†’
 934â†’  const { tasksRunner, runnerOptions } = getRunner(nxArgs, nxJson);
 935â†’
 936â†’  let hasher = createTaskHasher(projectGraph, nxJson, runnerOptions);
 937â†’
 938â†’  // this is used for two reasons: to fetch all remote cache hits AND
 939â†’  // to submit everything that is known in advance to Nx Cloud to run in
 940â†’  // a distributed fashion
 941â†’
 942â†’  await hashTasksThatDoNotDependOnOutputsOfOtherTasks(
 943â†’    hasher,
 944â†’    projectGraph,
 945â†’    taskGraph,
 946â†’    nxJson,
 947â†’    taskDetails
 948â†’  );
 949â†’  const taskResultsLifecycle = new TaskResultsLifeCycle();
 950â†’  const compositedLifeCycle: LifeCycle = new CompositeLifeCycle([
 951â†’    ...constructLifeCycles(lifeCycle),
 952â†’    taskResultsLifecycle,
 953â†’  ]);
 954â†’
 955â†’  let promiseOrObservable:
 956â†’    | Observable<{ task: Task; success: boolean }>
 957â†’    | Promise<{ [id: string]: TaskStatus }> = tasksRunner(
 958â†’    tasks,
 959â†’    {
 960â†’      ...runnerOptions,
 961â†’      lifeCycle: compositedLifeCycle,
 962â†’    },
 963â†’    {
 964â†’      initiatingProject,
 965â†’      initiatingTasks,
 966â†’      projectGraph,
 967â†’      nxJson,
 968â†’      nxArgs,
 969â†’      taskGraph,
 970â†’      hasher: {
 971â†’        hashTask(task: Task, taskGraph_?: TaskGraph, env?: NodeJS.ProcessEnv) {
 972â†’          if (!taskGraph_) {
 973â†’            output.warn({
 974â†’              title: `TaskGraph is now required as an argument to hashTask`,
 975â†’              bodyLines: [
 976â†’                `The TaskGraph object can be retrieved from the context`,
 977â†’                'This will result in an error in Nx 20',
 978â†’              ],
 979â†’            });
 980â†’            taskGraph_ = taskGraph;
 981â†’          }
 982â†’          if (!env) {
 983â†’            output.warn({
 984â†’              title: `The environment variables are now required as an argument to hashTask`,
 985â†’              bodyLines: [
 986â†’                `Please pass the environment variables used when running the task`,
 987â†’                'This will result in an error in Nx 20',
 988â†’              ],
 989â†’            });
 990â†’            env = process.env;
 991â†’          }
 992â†’          return hasher.hashTask(task, taskGraph_, env);
 993â†’        },
 994â†’        hashTasks(
 995â†’          task: Task[],
 996â†’          taskGraph_?: TaskGraph,
 997â†’          env?: NodeJS.ProcessEnv
 998â†’        ) {
 999â†’          if (!taskGraph_) {
1000â†’            output.warn({
1001â†’              title: `TaskGraph is now required as an argument to hashTasks`,
1002â†’              bodyLines: [
1003â†’                `The TaskGraph object can be retrieved from the context`,
1004â†’                'This will result in an error in Nx 20',
1005â†’              ],
1006â†’            });
1007â†’            taskGraph_ = taskGraph;
1008â†’          }
1009â†’          if (!env) {
1010â†’            output.warn({
1011â†’              title: `The environment variables are now required as an argument to hashTasks`,
1012â†’              bodyLines: [
1013â†’                `Please pass the environment variables used when running the tasks`,
1014â†’                'This will result in an error in Nx 20',
1015â†’              ],
1016â†’            });
1017â†’            env = process.env;
1018â†’          }
1019â†’
1020â†’          return hasher.hashTasks(task, taskGraph_, env);
1021â†’        },
1022â†’      },
1023â†’      daemon: daemonClient,
1024â†’    }
1025â†’  );
1026â†’  if ((promiseOrObservable as any).subscribe) {
1027â†’    promiseOrObservable = convertObservableToPromise(
1028â†’      promiseOrObservable as Observable<{ task: Task; success: boolean }>
1029â†’    );
1030â†’  }
1031â†’
1032â†’  await (promiseOrObservable as Promise<{
1033â†’    [id: string]: TaskStatus;
1034â†’  }>);
1035â†’  return taskResultsLifecycle.getTaskResults();
1036â†’}
1037â†’
1038â†’export function constructLifeCycles(lifeCycle: LifeCycle): LifeCycle[] {
1039â†’  const lifeCycles = [] as LifeCycle[];
1040â†’  lifeCycles.push(new StoreRunInformationLifeCycle());
1041â†’  lifeCycles.push(lifeCycle);
1042â†’  if (process.env.NX_PERF_LOGGING === 'true') {
1043â†’    lifeCycles.push(new TaskTimingsLifeCycle());
1044â†’  }
