1055â†’async function convertObservableToPromise(
1056â†’  obs: Observable<{ task: Task; success: boolean }>
1057â†’): Promise<{ [id: string]: TaskStatus }> {
1058â†’  return await new Promise((res) => {
1059â†’    let tasksResults: { [id: string]: TaskStatus } = {};
1060â†’    obs.subscribe({
1061â†’      next: (t) => {
