1097â†’function loadTasksRunner(modulePath: string): TasksRunner {
1098â†’  try {
1099â†’    const maybeTasksRunner = require(modulePath) as
1100â†’      | TasksRunner
1101â†’      | { default: TasksRunner };
1102â†’    // to support both babel and ts formats
1103â†’    return 'default' in maybeTasksRunner
1104â†’      ? maybeTasksRunner.default
1105â†’      : maybeTasksRunner;
1106â†’  } catch (e) {
1107â†’    if (
1108â†’      e.code === 'MODULE_NOT_FOUND' &&
1109â†’      (modulePath === 'nx-cloud' || modulePath === '@nrwl/nx-cloud')
1110â†’    ) {
1111â†’      return require('../nx-cloud/nx-cloud-tasks-runner-shell')
1112â†’        .nxCloudTasksRunnerShell;
1113â†’    }
1114â†’    throw e;
1115â†’  }
1116â†’}
