1118â†’export function getRunner(
1119â†’  nxArgs: NxArgs,
1120â†’  nxJson: NxJsonConfiguration
1121â†’): {
1122â†’  tasksRunner: TasksRunner;
1123â†’  runnerOptions: any;
1124â†’} {
1125â†’  let runner = nxArgs.runner;
1126â†’  runner = runner ?? 'default';
1127â†’
1128â†’  if (runner !== 'default' && !nxJson.tasksRunnerOptions?.[runner]) {
1129â†’    throw new Error(`Could not find runner configuration for ${runner}`);
1130â†’  }
1131â†’
1132â†’  const modulePath: string = getTasksRunnerPath(runner, nxJson);
1133â†’
1134â†’  try {
1135â†’    const tasksRunner = loadTasksRunner(modulePath);
1136â†’
1137â†’    return {
1138â†’      tasksRunner,
1139â†’      runnerOptions: getRunnerOptions(
1140â†’        runner,
1141â†’        nxJson,
1142â†’        nxArgs,
1143â†’        modulePath === 'nx-cloud'
1144â†’      ),
1145â†’    };
1146â†’  } catch {
