1151â†’const defaultTasksRunnerPath = require.resolve('./default-tasks-runner');
1152â†’
1153â†’function getTasksRunnerPath(
1154â†’  runner: string,
1155â†’  nxJson: NxJsonConfiguration<string[] | '*'>
1156â†’) {
1157â†’  // If running inside of Codex, there will be no internet access, so we cannot use the cloud runner, regardless of other config.
1158â†’  // We can infer this scenario by checking for certain environment variables defined in their base image: https://github.com/openai/codex-universal
1159â†’  if (process.env.CODEX_ENV_NODE_VERSION) {
1160â†’    output.warn({
1161â†’      title: 'Codex environment detected, using default tasks runner',
1162â†’      bodyLines: [
1163â†’        'Codex does not have internet access when it runs tasks, so Nx will use the default tasks runner and only leverage local caching.',
1164â†’      ],
1165â†’    });
1166â†’    return defaultTasksRunnerPath;
1167â†’  }
1168â†’
1169â†’  const isCloudRunner =
1170â†’    // No tasksRunnerOptions for given --runner
1171â†’    nxJson.nxCloudAccessToken ||
1172â†’    // No runner prop in tasks runner options, check if access token is set.
1173â†’    nxJson.tasksRunnerOptions?.[runner]?.options?.accessToken ||
1174â†’    ['nx-cloud', '@nrwl/nx-cloud'].includes(
1175â†’      nxJson.tasksRunnerOptions?.[runner]?.runner
1176â†’    ) ||
1177â†’    // Cloud access token specified in env var.
1178â†’    process.env.NX_CLOUD_ACCESS_TOKEN ||
1179â†’    // Nx Cloud ID specified in nxJson
1180â†’    nxJson.nxCloudId;
1181â†’
1182â†’  return isCloudRunner ? 'nx-cloud' : defaultTasksRunnerPath;
1183â†’}
1184â†’
1185â†’export function getRunnerOptions(
1186â†’  runner: string,
1187â†’  nxJson: NxJsonConfiguration<string[] | '*'>,
1188â†’  nxArgs: NxArgs,
1189â†’  isCloudDefault: boolean
1190â†’): any {
1191â†’  const defaultCacheableOperations = [];
1192â†’
1193â†’  for (const key in nxJson.targetDefaults) {
1194â†’    if (nxJson.targetDefaults[key].cache) {
1195â†’      defaultCacheableOperations.push(key);
1196â†’    }
1197â†’  }
1198â†’
1199â†’  const result = {
1200â†’    ...nxJson.tasksRunnerOptions?.[runner]?.options,
1201â†’    ...nxArgs,
1202â†’  };
1203â†’
1204â†’  // NOTE: we don't pull from env here because the cloud package
1205â†’  // supports it within nx-cloud's implementation. We could
1206â†’  // normalize it here, and that may make more sense, but
1207â†’  // leaving it as is for now.
1208â†’  if (nxJson.nxCloudAccessToken && isCloudDefault) {
1209â†’    result.accessToken ??= nxJson.nxCloudAccessToken;
1210â†’  }
1211â†’
1212â†’  if (nxJson.nxCloudId && isCloudDefault) {
1213â†’    result.nxCloudId ??= nxJson.nxCloudId;
1214â†’  }
1215â†’
1216â†’  if (nxJson.nxCloudUrl && isCloudDefault) {
1217â†’    result.url ??= nxJson.nxCloudUrl;
1218â†’  }
1219â†’
1220â†’  if (nxJson.nxCloudEncryptionKey && isCloudDefault) {
1221â†’    result.encryptionKey ??= nxJson.nxCloudEncryptionKey;
1222â†’  }
1223â†’
1224â†’  if (nxJson.parallel) {
1225â†’    result.parallel ??= nxJson.parallel;
1226â†’  }
1227â†’
1228â†’  if (nxJson.cacheDirectory) {
1229â†’    result.cacheDirectory ??= nxJson.cacheDirectory;
1230â†’  }
1231â†’
1232â†’  if (defaultCacheableOperations.length) {
1233â†’    result.cacheableOperations ??= [];
1234â†’    result.cacheableOperations = result.cacheableOperations.concat(
1235â†’      defaultCacheableOperations
1236â†’    );
1237â†’  }
1238â†’
1239â†’  if (nxJson.useDaemonProcess !== undefined) {
