 182â†’function hasPath(
 183â†’  graph: ProjectGraph,
 184â†’  target: string,
 185â†’  node: string,
 186â†’  visited: string[]
 187â†’) {
 188â†’  if (target === node) return true;
 189â†’
 190â†’  for (let d of graph.dependencies[node] || []) {
 191â†’    if (visited.indexOf(d.target) > -1) continue;
 192â†’    visited.push(d.target);
 193â†’    if (hasPath(graph, target, d.target, visited)) return true;
 194â†’  }
 195â†’  return false;
 196â†’}
