function parseTaskId(taskId: string): {
  projectName: string;
  targetName: string;
  configuration?: string;
} {
  const parts = taskId.split(':');

  if (parts.length < 2) {
    throw new Error(`Invalid task ID format: ${taskId}`);
  }

  if (parts.length === 2) {
    // No configuration: projectName:targetName
    return {
      projectName: parts[0],
      targetName: parts[1],
    };
  } else {
    // With configuration: projectName:targetName:configuration
    // Since project names can contain colons, we parse from the end
    const configuration = parts[parts.length - 1];
    const targetName = parts[parts.length - 2];
    const projectName = parts.slice(0, -2).join(':');

    return {
      projectName,
      targetName,
      configuration,
    };
  }
}

