 173â†’function projectExists(
 174â†’  projects: ProjectGraphProjectNode[],
 175â†’  projectToFind: string
 176â†’) {
 177â†’  return (
 178â†’    projects.find((project) => project.name === projectToFind) !== undefined
 179â†’  );
 180â†’}
 181â†’
 182â†’function hasPath(
 183â†’  graph: ProjectGraph,
 184â†’  target: string,
 185â†’  node: string,
 186â†’  visited: string[]
 187â†’) {
 188â†’  if (target === node) return true;
 189â†’
 190â†’  for (let d of graph.dependencies[node] || []) {
 191â†’    if (visited.indexOf(d.target) > -1) continue;
 192â†’    visited.push(d.target);
 193â†’    if (hasPath(graph, target, d.target, visited)) return true;
 194â†’  }
 195â†’  return false;
 196â†’}
 197â†’
 198â†’function filterGraph(
 199â†’  graph: ProjectGraph,
 200â†’  focus: string,
 201â†’  exclude: string[]
 202â†’): ProjectGraph {
 203â†’  let projectNames = (
 204â†’    Object.values(graph.nodes) as ProjectGraphProjectNode[]
 205â†’  ).map((project) => project.name);
 206â†’
 207â†’  let filteredProjectNames: Set<string>;
 208â†’
 209â†’  if (focus !== null) {
 210â†’    filteredProjectNames = new Set<string>();
 211â†’    projectNames.forEach((p) => {
 212â†’      const isInPath =
 213â†’        hasPath(graph, p, focus, []) || hasPath(graph, focus, p, []);
 214â†’
 215â†’      if (isInPath) {
 216â†’        filteredProjectNames.add(p);
 217â†’      }
 218â†’    });
 219â†’  } else {
 220â†’    filteredProjectNames = new Set<string>(projectNames);
 221â†’  }
 222â†’
 223â†’  if (exclude.length !== 0) {
 224â†’    exclude.forEach((p) => filteredProjectNames.delete(p));
 225â†’  }
 226â†’
 227â†’  let filteredGraph: ProjectGraph = {
 228â†’    nodes: {},
 229â†’    dependencies: {},
 230â†’  };
 231â†’
 232â†’  filteredProjectNames.forEach((p) => {
 233â†’    filteredGraph.nodes[p] = graph.nodes[p];
 234â†’    filteredGraph.dependencies[p] = graph.dependencies[p];
 235â†’  });
 236â†’
 237â†’  return filteredGraph;
 238â†’}
