 240â†’export async function generateGraph(
 241â†’  args: {
 242â†’    file?: string;
 243â†’    print?: boolean;
 244â†’    host?: string;
 245â†’    port?: number;
 246â†’    groupByFolder?: boolean;
 247â†’    watch?: boolean;
 248â†’    open?: boolean;
 249â†’    view: 'projects' | 'tasks' | 'project-details';
 250â†’    projects?: string[];
 251â†’    all?: boolean;
 252â†’    targets?: string[];
 253â†’    focus?: string;
 254â†’    exclude?: string[];
 255â†’    affected?: boolean;
 256â†’  },
 257â†’  affectedProjects: string[]
 258â†’): Promise<void> {
 259â†’  if (
 260â†’    Array.isArray(args.targets) &&
 261â†’    args.targets.length > 1 &&
 262â†’    args.file &&
 263â†’    !(args.file === 'stdout' || args.file.endsWith('.json'))
 264â†’  ) {
 265â†’    output.warn({
 266â†’      title: 'Showing Multiple Targets is not supported yet',
 267â†’      bodyLines: [
 268â†’        `Only the task graph for "${args.targets[0]}" tasks will be shown`,
 269â†’      ],
 270â†’    });
 271â†’  }
 272â†’
 273â†’  if (args.view === 'project-details' && !args.focus) {
 274â†’    output.error({
 275â†’      title: `The project details view requires the --focus option.`,
 276â†’    });
 277â†’    process.exit(1);
 278â†’  }
 279â†’  if (args.view === 'project-details' && (args.targets || args.affected)) {
 280â†’    output.error({
 281â†’      title: `The project details view can only be used with the --focus option.`,
 282â†’      bodyLines: [
 283â†’        `You passed ${args.targets ? '--targets ' : ''}${
 284â†’          args.affected ? '--affected ' : ''
 285â†’        }`,
 286â†’      ],
 287â†’    });
 288â†’    process.exit(1);
 289â†’  }
 290â†’
 291â†’  // TODO: Graph Client should support multiple targets
 292â†’  const target = Array.isArray(args.targets && args.targets.length >= 1)
 293â†’    ? args.targets[0]
 294â†’    : args.targets;
 295â†’
 296â†’  let rawGraph: ProjectGraph;
 297â†’  let sourceMaps: ConfigurationSourceMaps;
 298â†’  let isPartial = false;
 299â†’  try {
 300â†’    const projectGraphAndSourceMaps =
 301â†’      await createProjectGraphAndSourceMapsAsync({
 302â†’        exitOnError: false,
 303â†’      });
 304â†’    rawGraph = projectGraphAndSourceMaps.projectGraph;
 305â†’    sourceMaps = projectGraphAndSourceMaps.sourceMaps;
 306â†’  } catch (e) {
 307â†’    if (e instanceof ProjectGraphError) {
 308â†’      rawGraph = e.getPartialProjectGraph();
 309â†’      sourceMaps = e.getPartialSourcemaps();
 310â†’
 311â†’      isPartial = true;
 312â†’    }
 313â†’    if (!rawGraph) {
 314â†’      handleProjectGraphError({ exitOnError: true }, e);
 315â†’    } else {
 316â†’      const errors = e.getErrors();
 317â†’      if (errors?.length > 0) {
 318â†’        errors.forEach((e) => {
 319â†’          output.error({
 320â†’            title: e.message,
 321â†’            bodyLines: [e.stack],
 322â†’          });
 323â†’        });
 324â†’      }
 325â†’      output.warn({
 326â†’        title: `${
 327â†’          errors?.length > 1 ? `${errors.length} errors` : `An error`
 328â†’        } occured while processing the project graph. Showing partial graph.`,
 329â†’      });
 330â†’    }
 331â†’  }
 332â†’  let prunedGraph = pruneExternalNodes(rawGraph);
 333â†’
 334â†’  const projects = Object.values(
 335â†’    prunedGraph.nodes
 336â†’  ) as ProjectGraphProjectNode[];
 337â†’  projects.sort((a, b) => {
 338â†’    return a.name.localeCompare(b.name);
 339â†’  });
 340â†’
 341â†’  if (args.focus) {
 342â†’    if (!projectExists(projects, args.focus)) {
 343â†’      output.error({
 344â†’        title: `Project to focus does not exist.`,
 345â†’        bodyLines: [`You provided --focus=${args.focus}`],
 346â†’      });
 347â†’      process.exit(1);
 348â†’    }
 349â†’  }
 350â†’
 351â†’  if (args.affected) {
 352â†’    affectedProjects = (
 353â†’      await getAffectedGraphNodes(
 354â†’        splitArgsIntoNxArgsAndOverrides(
 355â†’          args,
 356â†’          'affected',
 357â†’          { printWarnings: !args.print && args.file !== 'stdout' },
 358â†’          readNxJson()
 359â†’        ).nxArgs,
 360â†’        rawGraph
 361â†’      )
 362â†’    ).map((n) => n.name);
 363â†’  }
 364â†’
 365â†’  if (args.exclude) {
 366â†’    const invalidExcludes: string[] = [];
 367â†’
 368â†’    args.exclude.forEach((project) => {
 369â†’      if (!projectExists(projects, project)) {
 370â†’        invalidExcludes.push(project);
 371â†’      }
 372â†’    });
 373â†’
 374â†’    if (invalidExcludes.length > 0) {
 375â†’      output.error({
 376â†’        title: `The following projects provided to --exclude do not exist:`,
 377â†’        bodyLines: invalidExcludes,
 378â†’      });
 379â†’      process.exit(1);
 380â†’    }
 381â†’  }
 382â†’
 383â†’  let html = readFileSync(
 384â†’    join(__dirname, '../../core/graph/index.html'),
 385â†’    'utf-8'
 386â†’  );
 387â†’
 388â†’  prunedGraph = filterGraph(
 389â†’    prunedGraph,
 390â†’    args.focus || null,
 391â†’    args.exclude || []
 392â†’  );
 393â†’
 394â†’  if (args.print || args.file === 'stdout') {
 395â†’    console.log(
 396â†’      JSON.stringify(
 397â†’        await createJsonOutput(
 398â†’          prunedGraph,
 399â†’          rawGraph,
 400â†’          args.projects,
 401â†’          args.targets
 402â†’        ),
 403â†’        null,
 404â†’        2
 405â†’      )
 406â†’    );
 407â†’    await output.drain();
 408â†’    process.exit(0);
 409â†’  }
 410â†’
 411â†’  if (args.file) {
 412â†’    const workspaceFolder = workspaceRoot;
 413â†’    const ext = extname(args.file);
 414â†’    const fullFilePath = isAbsolute(args.file)
 415â†’      ? args.file
 416â†’      : join(workspaceFolder, args.file);
 417â†’    const fileFolderPath = dirname(fullFilePath);
 418â†’
 419â†’    if (ext === '.html') {
 420â†’      const assetsFolder = join(fileFolderPath, 'static');
 421â†’      const assets: string[] = [];
 422â†’      cpSync(join(__dirname, '../../core/graph'), assetsFolder, {
 423â†’        filter: (_src, dest) => {
 424â†’          const isntHtml = !/index\.html/.test(dest);
 425â†’          if (isntHtml && dest.includes('.')) {
 426â†’            assets.push(dest);
 427â†’          }
 428â†’          return isntHtml;
 429â†’        },
 430â†’        recursive: true,
 431â†’      });
 432â†’
 433â†’      const { projectGraphClientResponse } =
 434â†’        await createProjectGraphAndSourceMapClientResponse(affectedProjects);
 435â†’
 436â†’      const taskGraphClientResponse = await createTaskGraphClientResponse();
 437â†’      const taskInputsReponse = await createExpandedTaskInputResponse(
 438â†’        taskGraphClientResponse,
 439â†’        projectGraphClientResponse
 440â†’      );
 441â†’
 442â†’      const environmentJs = buildEnvironmentJs(
 443â†’        args.exclude || [],
 444â†’        args.watch,
 445â†’        !!args.file && args.file.endsWith('html') ? 'build' : 'serve',
 446â†’        projectGraphClientResponse,
 447â†’        taskGraphClientResponse,
 448â†’        taskInputsReponse,
 449â†’        sourceMaps
 450â†’      );
 451â†’      html = html.replace(/src="/g, 'src="static/');
 452â†’      html = html.replace(/href="styles/g, 'href="static/styles');
 453â†’      html = html.replace(/<base href="\/".*>/g, '');
 454â†’      html = html.replace(/type="module"/g, '');
 455â†’
 456â†’      writeFileSync(fullFilePath, html);
 457â†’      writeFileSync(join(assetsFolder, 'environment.js'), environmentJs);
 458â†’
 459â†’      output.success({
 460â†’        title: `HTML output created in ${fileFolderPath}`,
 461â†’        bodyLines: [fileFolderPath, ...assets],
 462â†’      });
 463â†’    } else if (ext === '.json') {
 464â†’      mkdirSync(dirname(fullFilePath), { recursive: true });
 465â†’
 466â†’      const json = await createJsonOutput(
 467â†’        prunedGraph,
 468â†’        rawGraph,
 469â†’        args.projects,
 470â†’        args.targets
 471â†’      );
 472â†’
 473â†’      writeJsonFile(fullFilePath, json);
 474â†’
 475â†’      output.success({
 476â†’        title: `JSON output created in ${fileFolderPath}`,
 477â†’        bodyLines: [fullFilePath],
 478â†’      });
 479â†’    } else {
 480â†’      output.error({
 481â†’        title: `Please specify a filename with either .json or .html extension.`,
 482â†’        bodyLines: [`You provided --file=${args.file}`],
 483â†’      });
 484â†’      process.exit(1);
 485â†’    }
 486â†’    process.exit(0);
 487â†’  } else {
 488â†’    const environmentJs = buildEnvironmentJs(
 489â†’      args.exclude || [],
 490â†’      args.watch,
 491â†’      !!args.file && args.file.endsWith('html') ? 'build' : 'serve'
 492â†’    );
 493â†’
 494â†’    let app: Server;
 495â†’    let url: URL;
 496â†’    try {
 497â†’      const result = await startServer(
 498â†’        html,
 499â†’        environmentJs,
 500â†’        args.host || '127.0.0.1',
 501â†’        args.port || 4211,
 502â†’        args.watch,
 503â†’        affectedProjects,
 504â†’        args.focus,
 505â†’        args.groupByFolder,
 506â†’        args.exclude
 507â†’      );
 508â†’      app = result.app;
 509â†’      url = result.url;
 510â†’    } catch (err) {
 511â†’      output.error({
 512â†’        title: 'Failed to start graph server',
 513â†’        bodyLines: [err.message],
 514â†’      });
 515â†’      process.exit(1);
 516â†’    }
 517â†’
 518â†’    url.pathname = args.view;
 519â†’
 520â†’    if (args.focus) {
 521â†’      url.pathname += '/' + encodeURIComponent(args.focus);
 522â†’    }
 523â†’
 524â†’    if (target) {
 525â†’      url.pathname += '/' + target;
 526â†’    }
 527â†’    if (args.all) {
 528â†’      url.pathname += '/all';
 529â†’    } else if (args.projects) {
 530â†’      url.searchParams.append(
 531â†’        'projects',
 532â†’        args.projects.map((projectName) => projectName).join(' ')
 533â†’      );
 534â†’    } else if (args.affected) {
 535â†’      url.pathname += '/affected';
 536â†’    }
 537â†’    if (args.groupByFolder) {
 538â†’      url.searchParams.append('groupByFolder', 'true');
 539â†’    }
 540â†’
 541â†’    output.success({
 542â†’      title: `Project graph started at ${url.toString()}`,
 543â†’    });
 544â†’
 545â†’    if (args.open) {
 546â†’      open(url.toString());
 547â†’    }
 548â†’
 549â†’    return new Promise((res) => {
 550â†’      app.once('close', res);
 551â†’    });
 552â†’  }
 553â†’}
